import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { api } from '../services/api';

export default function Header() {
    const { user, loading, logout } = useAuth();
    const [open, setOpen] = useState(false);

    const genInvite = async () => {
        try {
            const { data } = await api.post('/invite/new', { ttlMinutes: 10 });
            const until = new Date(data.expiresAt).toLocaleString();
            alert(`Інвайт-код: ${data.code}\nДіє до: ${until}\n(одноразовий)`);
        } catch (e) {
            const msg = e.response?.data?.message || 'Не вдалося створити інвайт';
            alert(msg);
        } finally {
            setOpen(false);
        }
    };

    return (
        <header className="bg-blue-800 text-white">
            <div className="max-w-6xl mx-auto flex items-center justify-between py-3">
                <Link to="/" className="font-semibold text-lg">ХНУ імені В.Н. Каразіна</Link>

                <nav className="flex items-center gap-4">
                    <Link to="/" className="hover:underline">Головна</Link>
                    <Link to="/search" className="hover:underline">Пошук дипломів</Link>
                    <Link to="/add" className="hover:underline">Додати диплом</Link>

                    {!loading && user?.role === 'admin' && (
                        <div className="relative">
                            <button
                                onClick={() => setOpen(v => !v)}
                                className="px-3 py-1 bg-blue-700 rounded hover:bg-blue-600"
                            >
                                Адмін
                            </button>

                            {open && (
                                <div
                                    className="absolute right-0 mt-2 w-56 bg-white text-gray-800 rounded shadow z-50"
                                    onMouseLeave={() => setOpen(false)}
                                >
                                    <Link to="/admin" onClick={() => setOpen(false)} className="block px-3 py-2 hover:bg-gray-50">
                                        Панель адміністратора
                                    </Link>
                                    <Link to="/add" onClick={() => setOpen(false)} className="block px-3 py-2 hover:bg-gray-50">
                                        Додати диплом
                                    </Link>
                                    <Link to="/search?is_verified=false" onClick={() => setOpen(false)} className="block px-3 py-2 hover:bg-gray-50">
                                        Непідтверджені
                                    </Link>

                                    <div className="h-px bg-gray-200 my-1" />

                                    <button
                                        onClick={genInvite}
                                        className="block w-full text-left px-3 py-2 hover:bg-gray-50"
                                    >
                                        Згенерувати інвайт (10 хв)
                                    </button>

                                    <div className="h-px bg-gray-200 my-1" />

                                    <button
                                        onClick={logout}
                                        className="w-full text-left px-3 py-2 hover:bg-gray-50 text-red-600"
                                    >
                                        Вийти
                                    </button>
                                </div>
                            )}
                        </div>
                    )}

                    {!loading && !user && (
                        <>
                            <Link to="/register" className="hover:underline">Реєстрація</Link>
                            <Link to="/login" className="px-3 py-1 bg-red-600 rounded hover:bg-red-500">Вхід</Link>
                        </>
                    )}
                </nav>
            </div>
        </header>
    );
}
