import React, { useEffect, useMemo, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import FilterBar from '../components/FilterBar';
import DiplomaTable from '../components/DiplomaTable';
import { getDiplomas, getFilters, verifyDiploma, deleteDiploma } from '../services/diplomaService';
import { useAuth } from '../hooks/useAuth';

export default function SearchPage() {
    const { isAdmin, isAuthed } = useAuth();
    const [sp, setSp] = useSearchParams();

    // справочники
    const [opts, setOpts] = useState({
        facultiesOptions: [],
        specialtyOptionsByFacultyKey: {},
        specialtyToFaculty: {}, // { specialtyKey: facultyKey }
    });

    // состояние фильтров
    const [q, setQ] = useState(sp.get('q') || '');
    const [scope, setScope] = useState(sp.get('scope') || 'verified');
    const [sort, setSort] = useState(sp.get('sort') || 'createdAt');
    const [dir, setDir] = useState(sp.get('dir') || 'desc');
    const [facultyKey, setFacultyKey] = useState(sp.get('faculty') || '');
    const [specialty, setSpecialty] = useState(sp.get('specialty') || '');
    const [page, setPage] = useState(Number(sp.get('page') || 1));

    const [rows, setRows] = useState([]);
    const [total, setTotal] = useState(0);
    const [busy, setBusy] = useState(false);

    // загрузка справочников (один раз)
    useEffect(() => {
        (async () => {
            const f = await getFilters();

            // карта "спец-ть -> факультет"
            const stf = {};
            Object.entries(f.specialtyOptionsByFacultyKey || {}).forEach(([fk, arr]) =>
                (arr || []).forEach(s => { stf[s.key] = fk; })
            );

            setOpts({
                facultiesOptions: f.facultiesOptions || [],
                specialtyOptionsByFacultyKey: f.specialtyOptionsByFacultyKey || {},
                specialtyToFaculty: stf,
            });

            // если в URL только specialty — авто-выставим faculty
            const urlSpec = (sp.get('specialty') || '').trim();
            if (urlSpec && !sp.get('faculty')) {
                const fk = stf[urlSpec] || '';
                if (fk) setFacultyKey(fk);
            }
        })();
        // пустой массив зависимостей — запускаем только на маунт
    }, []); // ← без eslint-комментариев

    // каскад: смена факультета "выбрасывает" не принадлежащую ему спец-ть
    useEffect(() => {
        if (!facultyKey || !specialty) return;
        const fkOfSpec = opts.specialtyToFaculty[specialty];
        if (fkOfSpec && fkOfSpec !== facultyKey) setSpecialty('');
    }, [facultyKey, specialty, opts.specialtyToFaculty]);

    // выбор спец-ти автоматически определяет факультет
    useEffect(() => {
        if (!specialty) return;
        const fk = opts.specialtyToFaculty[specialty];
        if (fk && fk !== facultyKey) setFacultyKey(fk);
    }, [specialty, opts.specialtyToFaculty]); // умышленно без facultyKey, чтобы не зациклить

    // опции специализаций для выбранного факультета
    const specOptions = useMemo(
        () => (facultyKey ? (opts.specialtyOptionsByFacultyKey[facultyKey] || []) : []),
        [facultyKey, opts.specialtyOptionsByFacultyKey]
    );

    const fetchData = async () => {
        setBusy(true);
        try {
            const params = {
                q, sort, dir, page,
                is_verified: scope === 'verified' ? 'true' : (scope === 'unverified' ? 'false' : undefined),
                faculty: facultyKey || undefined,
                specialty: specialty || undefined,
            };
            const { rows, total } = await getDiplomas(params);
            setRows(rows || []);
            setTotal(total || 0);
        } finally {
            setBusy(false);
        }
    };

    // синхронизация URL ↔ state
    useEffect(() => {
        const p = new URLSearchParams();
        if (q) p.set('q', q);
        if (facultyKey) p.set('faculty', facultyKey);
        if (specialty) p.set('specialty', specialty);
        if (scope !== 'verified') p.set('scope', scope);
        if (sort !== 'createdAt') p.set('sort', sort);
        if (dir !== 'desc') p.set('dir', dir);
        if (page !== 1) p.set('page', String(page));
        setSp(p, { replace: true });
    }, [q, facultyKey, specialty, scope, sort, dir, page, setSp]);

    // загрузка данных при изменении фильтров
    useEffect(() => { fetchData(); }, [q, facultyKey, specialty, scope, sort, dir, page]);

    const onVerify = async (id, curr) => {
        if (!isAuthed || !isAdmin) return;
        setBusy(true);
        try { await verifyDiploma(id, !curr); await fetchData(); }
        finally { setBusy(false); }
    };

    const onDelete = async (id) => {
        if (!isAuthed || !isAdmin) return;
        if (!window.confirm('Видалити запис?')) return;
        setBusy(true);
        try { await deleteDiploma(id); await fetchData(); }
        finally { setBusy(false); }
    };

    return (
        <div className="space-y-6">
            <FilterBar
                q={q} setQ={setQ}
                scope={scope} setScope={setScope}
                sort={sort} setSort={setSort}
                dir={dir} setDir={setDir}
                facultyKey={facultyKey} setFacultyKey={setFacultyKey}
                specialty={specialty} setSpecialty={setSpecialty}
                facultiesOptions={opts.facultiesOptions}
                specialtyOptions={specOptions}
                onRefresh={fetchData}
                allowUnverified={isAdmin}
            />

            <DiplomaTable
                rows={rows}
                total={total}
                page={page}
                setPage={setPage}
                busy={busy}
                onVerify={onVerify}
                onDelete={onDelete}
            />
        </div>
    );
}
