import React, { useMemo } from 'react';

export default function FilterBar({
                                      q, setQ,
                                      sort, setSort,
                                      dir, setDir,
                                      facultyKey, setFacultyKey,
                                      specialty, setSpecialty,
                                      facultiesOptions = [],
                                      specialtyOptions = [],
                                      // админ: показать селектор области (всі/підтв./непідтв.)
                                      showScope = false,
                                      scope, setScope,
                                      onRefresh = () => {},
                                      onReset,
                                  }) {
    const sortOptions = useMemo(() => ([
        { value: 'createdAt', label: 'За створенням' },
        { value: 'year', label: 'За роком' },
        { value: 'studentName', label: 'ПІБ' },
        { value: 'diplomaNumber', label: '№ диплому' },
    ]), []);

    const dirOptions = useMemo(() => ([
        { value: 'desc', label: 'Спадання' },
        { value: 'asc',  label: 'Зростання' },
    ]), []);

    const internalReset = () => {
        setQ('');
        setFacultyKey('');
        setSpecialty('');
        setSort('createdAt');
        setDir('desc');
        if (showScope && setScope) setScope('all');
        onRefresh();
    };

    const doReset = onReset || internalReset;

    return (
        <div className="w-full grid grid-cols-12 gap-3 items-stretch bg-white/75 p-3 rounded-xl shadow-sm">
            {/* Поиск + Знайти */}
            <div className="col-span-12 md:col-span-5 flex gap-2">
                <input
                    className="flex-1 h-10 rounded-md border border-gray-300 px-3"
                    type="text"
                    placeholder="Пошук (ПІБ або № диплому)…"
                    value={q}
                    onChange={(e) => setQ(e.target.value)}
                    onKeyDown={(e) => { if (e.key === 'Enter') onRefresh(); }}
                />
                <button
                    type="button"
                    onClick={onRefresh}
                    className="h-10 rounded-md bg-blue-600 text-white px-4"
                >
                    Знайти
                </button>
            </div>

            {/* Факультет */}
            <div className="col-span-12 sm:col-span-4 md:col-span-3">
                <select
                    className="w-full h-10 rounded-md border border-gray-300 px-3"
                    value={facultyKey}
                    onChange={(e) => setFacultyKey(e.target.value)}
                >
                    <option value="">Усі факультети</option>
                    {facultiesOptions.map(f => (
                        <option key={f.key} value={f.key}>{f.name}</option>
                    ))}
                </select>
            </div>

            {/* Спеціальність */}
            <div className="col-span-12 sm:col-span-4 md:col-span-4">
                <select
                    className="w-full h-10 rounded-md border border-gray-300 px-3"
                    value={specialty}
                    onChange={(e) => setSpecialty(e.target.value)}
                >
                    <option value="">Усі спеціальності</option>
                    {specialtyOptions.map(s => (
                        <option key={s.key} value={s.key}>{s.label}</option>
                    ))}
                </select>
            </div>

            {/* Только админ видит селектор области */}
            {showScope && (
                <div className="col-span-12 sm:col-span-6 md:col-span-3">
                    <select
                        className="w-full h-10 rounded-md border border-gray-300 px-3"
                        value={scope}
                        onChange={(e) => setScope(e.target.value)}
                    >
                        <option value="all">Всі</option>
                        <option value="verified">Лише підтверджені</option>
                        <option value="unverified">Лише непідтверджені</option>
                    </select>
                </div>
            )}

            {/* Сортировки */}
            <div className="col-span-6 sm:col-span-6 md:col-span-2">
                <select
                    className="w-full h-10 rounded-md border border-gray-300 px-3"
                    value={sort}
                    onChange={(e) => setSort(e.target.value)}
                >
                    {sortOptions.map(o => <option key={o.value} value={o.value}>{o.label}</option>)}
                </select>
            </div>
            <div className="col-span-6 sm:col-span-6 md:col-span-2">
                <select
                    className="w-full h-10 rounded-md border border-gray-300 px-3"
                    value={dir}
                    onChange={(e) => setDir(e.target.value)}
                >
                    {dirOptions.map(o => <option key={o.value} value={o.value}>{o.label}</option>)}
                </select>
            </div>

            {/* Действия */}
            <div className="col-span-12 md:col-span-2 flex gap-2 justify-end">
                <button
                    type="button"
                    onClick={onRefresh}
                    className="h-10 rounded-md bg-sky-600 text-white px-4"
                >
                    Оновити
                </button>
                <button
                    type="button"
                    onClick={doReset}
                    className="h-10 rounded-md border border-gray-300 px-4"
                >
                    Скинути
                </button>
            </div>
        </div>
    );
}
