import React, { useEffect, useState } from 'react';
import { getDiplomas, updateDiploma, deleteDiploma } from '../services/diplomaService';
import EditDiplomaModal from '../components/EditDiplomaModal';

export default function AdminPage() {
  const [diplomas, setDiplomas] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    try {
      const data = await getDiplomas({ is_verified: 'false', page: 1, limit: 50 });
      setDiplomas(data.items || data);
    } catch {
    }
    setLoading(false);
  };

  useEffect(() => { fetchData(); }, []);

  const handleVerify = async (id) => {
    await updateDiploma(id, { isVerified: true });
    fetchData();
  };

  const handleDelete = async (id) => {
    await deleteDiploma(id);
    fetchData();
  };

  if (loading) return <p>Завантаження...</p>;

  return (
      <div className="max-w-6xl mx-auto">
        <h2 className="text-2xl font-semibold mb-4">Адмінка: непідтверджені дипломи</h2>
        <table className="min-w-full bg-white shadow rounded">
          <thead className="bg-gray-100">
          <tr>
            <th className="px-4 py-2 text-left">ПІБ</th>
            <th className="px-4 py-2 text-left">Спеціальність</th>
            <th className="px-4 py-2 text-left">Рік</th>
            <th className="px-4 py-2 text-left">Номер</th>
            <th className="px-4 py-2 text-left">Дії</th>
          </tr>
          </thead>
          <tbody>
          {diplomas.map(d => (
              <tr key={d.id} className="border-t">
                <td className="px-4 py-2">{d.studentName}</td>
                <td className="px-4 py-2">{d.specialty}</td>
                <td className="px-4 py-2">{d.year}</td>
                <td className="px-4 py-2">{d.diplomaNumber}</td>
                <td className="px-4 py-2 space-x-2">
                  <button onClick={() => handleVerify(d.id)} className="bg-green-600 text-white px-2 py-1 rounded hover:bg-green-700">
                    Підтвердити
                  </button>
                  <button onClick={() => handleDelete(d.id)} className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700">
                    Видалити
                  </button>
                </td>
              </tr>
          ))}
          {!diplomas.length && (
              <tr><td colSpan="5" className="px-4 py-6 text-center text-gray-500">Немає записів</td></tr>
          )}
          </tbody>
        </table>
      </div>
  );
}
