import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const location = useLocation();
  const linkClass = (path) =>
    `text-white hover:text-yellow-300 px-4 py-2 ${location.pathname === path ? 'font-bold underline' : ''}`;

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <header className="bg-blue-900 text-white p-4 flex justify-between items-center shadow-md">
      <div className="text-xl font-semibold">ХНУ імені В.Н. Каразіна</div>
      <nav>
        <Link to="/" className={linkClass('/')}>Головна</Link>
        <Link to="/search" className={linkClass('/search')}>Пошук дипломів</Link>
        <Link to="/register" className={linkClass('/register')}>Реєстрація</Link>
        <Link to="/login" className={linkClass('/login')}>Вхід</Link>
        <button onClick={handleLogout} className="ml-4 text-sm bg-red-600 px-3 py-1 rounded hover:bg-red-500">
          Вийти
        </button>
      </nav>
    </header>
  );
};

export default Header;
