import React, { useState } from 'react';

export default function FilterBar({ onFilter }) {
  const [values, setValues] = useState({ name: '', year: '', specialty: '' });

  const handleChange = (e) => setValues({ ...values, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    onFilter(values);
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4 flex flex-wrap gap-4 items-end bg-white p-4 shadow rounded">
      <input name="name" placeholder="ПІБ" onChange={handleChange} className="border rounded px-3 py-2" />
      <input name="year" type="number" placeholder="Рік" onChange={handleChange} className="border rounded px-3 py-2" />
      <input name="specialty" placeholder="Спеціальність" onChange={handleChange} className="border rounded px-3 py-2" />
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Знайти</button>
    </form>
  );
}
