import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import FilterBar from '../components/FilterBar';
import DiplomaTable from '../components/DiplomaTable';
import { getDiplomas, verifyDiploma, deleteDiploma } from '../services/diplomaService';

export default function SearchPage() {
    const [sp, setSp] = useSearchParams();

    const [q, setQ] = useState(sp.get('q') || '');
    const [scope, setScope] = useState(sp.get('scope') || 'all');
    const [sort, setSort] = useState(sp.get('sort') || 'createdAt');
    const [dir, setDir] = useState(sp.get('dir') || 'desc');

    const [items, setItems] = useState([]);
    const [total, setTotal] = useState(0);
    const [loading, setLoading] = useState(true);
    const [busy, setBusy] = useState(false);
    const [err, setErr] = useState('');

    const isAuthed = !!localStorage.getItem('token');

    const syncQS = () => {
        const next = new URLSearchParams();
        if (q) next.set('q', q);
        if (scope && scope !== 'all') next.set('scope', scope);
        if (sort && sort !== 'createdAt') next.set('sort', sort);
        if (dir && dir !== 'desc') next.set('dir', dir);
        setSp(next, { replace: true });
    };

    const fetchDiplomas = async () => {
        setLoading(true);
        setErr('');
        try {
            const params = {
                q,
                verified: scope === 'all' ? undefined : (scope === 'verified' ? 'true' : 'false'),
                sort,
                dir,
                page: 1,
                limit: 50,
            };
            const data = await getDiplomas(params);
            setItems(data.items || []);
            setTotal(data.total || 0);
        } catch (e) {
            setErr(e.response?.data?.message || 'Load error');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => { syncQS(); fetchDiplomas(); /* eslint-disable-next-line */ }, [q, scope, sort, dir]);

    const onVerify = async (id, curr) => {
        if (!isAuthed) return;
        setBusy(true);
        try {
            await verifyDiploma(id, !curr);
            await fetchDiplomas();
        } finally { setBusy(false); }
    };

    const onDelete = async (id) => {
        if (!isAuthed) return;
        if (!confirm('Видалити запис?')) return;
        setBusy(true);
        try {
            await deleteDiploma(id);
            await fetchDiplomas();
        } finally { setBusy(false); }
    };

    return (
        <div className="space-y-6">
            <h1 className="text-2xl font-bold">Пошук дипломів</h1>
            <FilterBar
                q={q} setQ={setQ}
                scope={scope} setScope={setScope}
                sort={sort} setSort={setSort}
                dir={dir} setDir={setDir}
                onRefresh={fetchDiplomas}
            />
            <DiplomaTable
                items={items}
                total={total}
                loading={loading}
                error={err}
                busy={busy}
                onVerify={onVerify}
                onDelete={onDelete}
                showActions={isAuthed}
            />
        </div>
    );
}
