import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../services/api';
import { updateDiplomaMultipart } from '../services/diplomaService';

const MAX_MB = 10;
const ALLOWED = new Set(['application/pdf', 'image/jpeg', 'image/png']);

export default function EditDiplomaModal({ open, onClose, item, onSaved }) {
    const [catalog, setCatalog] = useState([]);
    const [loading, setLoading] = useState(false);
    const [progress, setProgress] = useState(0);
    const [errors, setErrors] = useState({});
    const [msg, setMsg] = useState('');
    const [file, setFile] = useState(null);

    const [faculty, setFaculty] = useState('');
    useEffect(() => {
        api.get('/catalog/list').then(r => setCatalog(r.data || []));
    }, []);
    useEffect(() => {
        if (!item || !catalog.length) return;
        const f = catalog.find(f => f.specialties.some(s => s.name === item.specialty));
        setFaculty(f?.key || '');
    }, [item, catalog]);

    const specialties = useMemo(
        () => (catalog.find(f => f.key === faculty)?.specialties || []),
        [catalog, faculty]
    );

    const [form, setForm] = useState({
        studentName: item?.studentName || '',
        diplomaNumber: item?.diplomaNumber || '',
        year: String(item?.year || ''),
        specialtyCode: '',
        specialtyName: item?.specialty || '',
        isVerified: !!item?.isVerified,
    });
    useEffect(() => {
        if (!open) return;
        setForm({
            studentName: item?.studentName || '',
            diplomaNumber: item?.diplomaNumber || '',
            year: String(item?.year || ''),
            specialtyCode: '',
            specialtyName: item?.specialty || '',
            isVerified: !!item?.isVerified,
        });
        setFile(null); setErrors({}); setMsg(''); setProgress(0);
    }, [open, item]);

    useEffect(() => {
        if (!form.specialtyCode) return;
        const sp = specialties.find(s => s.code === form.specialtyCode);
        if (sp) setForm(f => ({ ...f, specialtyName: sp.name }));
    }, [form.specialtyCode, specialties]);

    const onChange = (e) => {
        const { name, value, type, checked } = e.target;
        setForm(s => ({ ...s, [name]: type === 'checkbox' ? checked : value }));
    };

    function onFile(e) {
        const f = e.target.files?.[0];
        if (!f) { setFile(null); return; }
        if (!ALLOWED.has(f.type)) { setErrors({ file: 'Дозволені PDF/JPG/PNG' }); e.target.value=''; return; }
        if (f.size > MAX_MB * 1024 * 1024) { setErrors({ file: `Файл > ${MAX_MB} МБ` }); e.target.value=''; return; }
        setErrors(prev => ({ ...prev, file: undefined }));
        setFile(f);
    }

    async function onSubmit(e) {
        e.preventDefault();
        setMsg(''); setErrors({}); setLoading(true);
        try {
            const fd = new FormData();
            if (form.studentName && form.studentName !== item.studentName) fd.append('studentName', form.studentName);
            if (form.diplomaNumber && form.diplomaNumber !== item.diplomaNumber) fd.append('diplomaNumber', form.diplomaNumber);
            if (form.year && Number(form.year) !== item.year) fd.append('year', form.year);
            if (form.specialtyName && form.specialtyName !== item.specialty) fd.append('specialty', form.specialtyName);
            if (form.isVerified !== item.isVerified) fd.append('isVerified', String(form.isVerified));
            if (file) fd.append('file', file);

            const updated = await updateDiplomaMultipart(item.id, fd, setProgress);
            onSaved?.(updated);
            onClose?.();
        } catch (err) {
            const data = err.response?.data;
            setMsg(data?.message || 'Помилка');
            setErrors(data?.errors || {});
        } finally {
            setLoading(false);
        }
    }

    if (!open) return null;

    return (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center" onMouseDown={onClose}>
            <div className="bg-white rounded shadow p-5 w-full max-w-xl" onMouseDown={e => e.stopPropagation()}>
                <h3 className="text-lg font-semibold mb-3">Редагувати диплом</h3>
                {msg && <p className="text-red-600 mb-2">{msg}</p>}
                <form onSubmit={onSubmit} className="space-y-3">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                        <div>
                            <label className="block text-sm mb-1">ПІБ</label>
                            <input name="studentName" className="border rounded px-3 py-2 w-full"
                                   value={form.studentName} onChange={onChange} />
                        </div>
                        <div>
                            <label className="block text-sm mb-1">Номер диплому</label>
                            <input name="diplomaNumber" className="border rounded px-3 py-2 w-full"
                                   value={form.diplomaNumber} onChange={onChange} />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                        <div>
                            <label className="block text-sm mb-1">Рік</label>
                            <input type="number" min="1900" max={new Date().getFullYear()}
                                   name="year" className="border rounded px-3 py-2 w-full"
                                   value={form.year} onChange={onChange} />
                        </div>
                        <div className="flex items-center gap-2 mt-6 md:mt-7">
                            <input id="v" type="checkbox" name="isVerified" checked={form.isVerified} onChange={onChange} />
                            <label htmlFor="v">Підтверджено</label>
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                        <div>
                            <label className="block text-sm mb-1">Факультет</label>
                            <select className="border rounded px-3 py-2 w-full" value={faculty}
                                    onChange={(e)=>{ setFaculty(e.target.value); setForm(f=>({...f, specialtyCode:'', specialtyName:''})); }}>
                                <option value="">— Оберіть —</option>
                                {catalog.map(f => <option key={f.key} value={f.key}>{f.name}</option>)}
                            </select>
                        </div>
                        <div>
                            <label className="block text-sm mb-1">Спеціальність</label>
                            <select className="border rounded px-3 py-2 w-full"
                                    value={form.specialtyCode}
                                    onChange={(e)=> setForm(s=>({ ...s, specialtyCode: e.target.value })) }
                                    disabled={!faculty}>
                                <option value="">{form.specialtyName ? `Залишити: ${form.specialtyName}` : '— Оберіть —'}</option>
                                {specialties.map(s => <option key={s.code} value={s.code}>{s.code} — {s.name}</option>)}
                            </select>
                            {errors.specialty && <p className="text-red-600 text-sm">{errors.specialty}</p>}
                        </div>
                    </div>

                    <div>
                        <label className="block text-sm mb-1">Змінити файл (PDF/JPG/PNG, ≤{MAX_MB} МБ)</label>
                        <input type="file" accept=".pdf,.jpg,.jpeg,.png" onChange={onFile} />
                        {errors.file && <p className="text-red-600 text-sm">{errors.file}</p>}
                    </div>

                    {loading && (
                        <div className="w-full bg-gray-200 rounded h-3">
                            <div className="h-3 rounded bg-blue-600" style={{ width: `${progress}%` }} />
                            <p className="text-sm mt-1">{progress}%</p>
                        </div>
                    )}

                    <div className="flex justify-end gap-2 pt-1">
                        <button type="button" onClick={onClose} className="px-4 py-2 border rounded">Скасувати</button>
                        <button className="px-4 py-2 bg-blue-600 text-white rounded disabled:opacity-50" disabled={loading}>
                            Зберегти
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
