import React, { useEffect, useState } from 'react';
import { getFacultiesTree } from '../services/diplomaService';
import { Link } from 'react-router-dom';

export default function HomePage() {
    const [rows, setRows] = useState([]);
    const [err, setErr] = useState('');

    useEffect(() => {
        (async () => {
            try {
                const { faculties } = await getFacultiesTree();
                setRows(faculties || []);
            } catch (e) {
                setErr(e.response?.data?.message || 'Load error');
            }
        })();
    }, []);

    return (
        <div className="space-y-6">
            <h1 className="text-2xl font-bold">Факультети</h1>
            {err && <div className="text-red-600">{err}</div>}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3">
                {rows.map(r => (
                    <Link
                        key={r.faculty}
                        to={`/faculty/${encodeURIComponent(r.faculty)}`}
                        className="border rounded p-4 bg-white/70 hover:bg-white transition"
                    >
                        <div className="font-semibold">{r.faculty}</div>
                        <div className="text-sm opacity-70">Записів: {r.count}</div>
                    </Link>
                ))}
                {rows.length === 0 && <div>Немає даних</div>}
            </div>
        </div>
    );
}
