import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import FilterBar from '../components/FilterBar';
import DiplomaTable from '../components/DiplomaTable';
import { getDiplomas, getFilters, verifyDiploma, deleteDiploma } from '../services/diplomaService';
import { useAuth } from '../hooks/useAuth';

export default function SearchPage() {
    const { isAdmin } = useAuth();
    const [sp, setSp] = useSearchParams();

    const [q, setQ] = useState(sp.get('q') || '');
    const [faculty, setFaculty] = useState(sp.get('faculty') || '');
    const [specialty, setSpecialty] = useState(sp.get('specialty') || '');
    const [scope, setScope] = useState(sp.get('scope') || (isAdmin ? 'all' : 'verified'));
    const [sort, setSort] = useState(sp.get('sort') || 'createdAt');
    const [dir, setDir] = useState(sp.get('dir') || 'desc');

    const [faculties, setFaculties] = useState([]);
    const [specialties, setSpecialties] = useState([]);

    const [items, setItems] = useState([]);
    const [total, setTotal] = useState(0);
    const [loading, setLoading] = useState(true);
    const [busy, setBusy] = useState(false);
    const [err, setErr] = useState('');

    const isAuthed = !!localStorage.getItem('token');

    const syncQS = () => {
        const next = new URLSearchParams();
        if (q) next.set('q', q);
        if (faculty) next.set('faculty', faculty);
        if (specialty) next.set('specialty', specialty);
        if (scope && scope !== (isAdmin ? 'all' : 'verified')) next.set('scope', scope);
        if (sort && sort !== 'createdAt') next.set('sort', sort);
        if (dir && dir !== 'desc') next.set('dir', dir);
        setSp(next, { replace: true });
    };

    const fetchFilters = async () => {
        try {
            const f = await getFilters();
            setFaculties(f.faculties || []);
            setSpecialties(f.specialties || []);
        } catch {}
    };

    const fetchDiplomas = async () => {
        setLoading(true);
        setErr('');
        try {
            const params = {
                q, faculty, specialty,
                verified: isAdmin ? (scope === 'all' ? undefined : (scope === 'verified' ? 'true' : 'false')) : 'true',
                sort, dir, page: 1, limit: 50,
            };
            const data = await getDiplomas(params);
            setItems(data.items || []);
            setTotal(data.total || 0);
        } catch (e) {
            setErr(e.response?.data?.message || 'Load error');
        } finally { setLoading(false); }
    };

    useEffect(() => { fetchFilters(); }, []);
    useEffect(() => { syncQS(); fetchDiplomas(); /* eslint-disable-next-line */ }, [q, faculty, specialty, scope, sort, dir, isAdmin]);

    const onVerify = async (id, curr) => {
        if (!isAuthed || !isAdmin) return;
        setBusy(true);
        try { await verifyDiploma(id, !curr); await fetchDiplomas(); } finally { setBusy(false); }
    };

    const onDelete = async (id) => {
        if (!isAuthed || !isAdmin) return;
        if (!confirm('Видалити запис?')) return;
        setBusy(true);
        try { await deleteDiploma(id); await fetchDiplomas(); } finally { setBusy(false); }
    };

    return (
        <div className="space-y-6">
            <h1 className="text-2xl font-bold">Пошук дипломів</h1>
            <FilterBar
                q={q} setQ={setQ}
                scope={scope} setScope={setScope}
                sort={sort} setSort={setSort}
                dir={dir} setDir={setDir}
                faculty={faculty} setFaculty={setFaculty}
                specialty={specialty} setSpecialty={setSpecialty}
                faculties={faculties} specialties={specialties}
                onRefresh={fetchDiplomas}
                allowUnverified={isAdmin}
            />
            <DiplomaTable
                items={items}
                total={total}
                loading={loading}
                error={err}
                busy={busy}
                onVerify={onVerify}
                onDelete={onDelete}
                showActions={isAdmin}
            />
        </div>
    );
}
