import React, { useEffect, useState } from 'react';
import { listInvites, createInvite, revokeInvite } from '../services/inviteService';
import { getDiplomas, verifyDiploma, deleteDiploma } from '../services/diplomaService';

export default function AdminPage() {
  const [q, setQ] = useState('');
  const [scope, setScope] = useState('unverified'); // unverified | verified | all
  const [sort, setSort] = useState('createdAt');
  const [dir, setDir] = useState('desc');

  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);

  const [invites, setInvites] = useState([]);
  const [invMinutes, setInvMinutes] = useState(10);
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState('');

  const fetchDiplomas = async () => {
    setLoading(true);
    setErr('');
    try {
      const params = {
        q,
        verified: scope === 'all' ? undefined : (scope === 'verified' ? 'true' : 'false'),
        sort,
        dir,
        page: 1,
        limit: 50,
      };
      const data = await getDiplomas(params);
      setItems(data.items || []);
      setTotal(data.total || 0);
    } catch (e) {
      setErr(e.response?.data?.message || 'Load error');
    } finally {
      setLoading(false);
    }
  };

  const fetchInvites = async () => {
    try {
      const rows = await listInvites();
      setInvites(rows);
    } catch { /* ignore for non-admin */ }
  };

  useEffect(() => { fetchDiplomas(); }, [q, scope, sort, dir]);
  useEffect(() => { fetchInvites(); }, []);

  const toggleVerify = async (id, curr) => {
    setBusy(true);
    try {
      await verifyDiploma(id, !curr);
      await fetchDiplomas();
    } finally { setBusy(false); }
  };

  const removeDiploma = async (id) => {
    if (!confirm('Видалити запис?')) return;
    setBusy(true);
    try {
      await deleteDiploma(id);
      await fetchDiplomas();
    } finally { setBusy(false); }
  };

  const addInvite = async () => {
    setBusy(true);
    try {
      await createInvite(Number(invMinutes) || 10);
      await fetchInvites();
    } finally { setBusy(false); }
  };

  const copyInvite = async (code) => {
    const link = `${location.origin}/register?code=${code}`;
    await navigator.clipboard.writeText(link);
    alert('Посилання скопійовано:\n' + link);
  };

  const revoke = async (id) => {
    if (!confirm('Відreзвати інвайт?')) return;
    setBusy(true);
    try {
      await revokeInvite(id);
      await fetchInvites();
    } finally { setBusy(false); }
  };

  return (
      <div className="space-y-8">
        <h1 className="text-2xl font-bold">Адмін-панель</h1>

        {/* Фільтр */}
        <div className="flex flex-wrap gap-2 items-center">
          <input value={q} onChange={e=>setQ(e.target.value)} placeholder="Пошук ПІБ/№/спец." className="border p-2 rounded"/>
          <select value={scope} onChange={e=>setScope(e.target.value)} className="border p-2 rounded">
            <option value="unverified">Неперевірені</option>
            <option value="verified">Перевірені</option>
            <option value="all">Усі</option>
          </select>
          <select value={sort} onChange={e=>setSort(e.target.value)} className="border p-2 rounded">
            <option value="createdAt">За створенням</option>
            <option value="year">Рік</option>
            <option value="studentName">ПІБ</option>
            <option value="diplomaNumber">№ диплому</option>
          </select>
          <select value={dir} onChange={e=>setDir(e.target.value)} className="border p-2 rounded">
            <option value="desc">↓</option>
            <option value="asc">↑</option>
          </select>
          <button onClick={fetchDiplomas} className="px-3 py-2 bg-gray-200 rounded">Оновити</button>
        </div>

        {err && <div className="text-red-600">{err}</div>}

        {/* Таблиця дипломів */}
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm border">
            <thead className="bg-gray-100">
            <tr>
              <th className="p-2 border">ПІБ</th>
              <th className="p-2 border">Спеціальність</th>
              <th className="p-2 border">Рік</th>
              <th className="p-2 border">№ диплому</th>
              <th className="p-2 border">Файл</th>
              <th className="p-2 border">Перевірено</th>
              <th className="p-2 border">Дії</th>
            </tr>
            </thead>
            <tbody>
            {loading ? (
                <tr><td className="p-3" colSpan={7}>Завантаження…</td></tr>
            ) : items.length === 0 ? (
                <tr><td className="p-3" colSpan={7}>Немає результатів</td></tr>
            ) : items.map(d => (
                <tr key={d.id} className="odd:bg-white even:bg-gray-50">
                  <td className="p-2 border">{d.studentName}</td>
                  <td className="p-2 border">{d.specialty || '-'}</td>
                  <td className="p-2 border">{d.year}</td>
                  <td className="p-2 border">{d.diplomaNumber}</td>
                  <td className="p-2 border">
                    <a className="text-blue-600 underline" href={d.fileUrl} target="_blank" rel="noreferrer">Відкрити</a>
                  </td>
                  <td className="p-2 border">
                    <button
                        disabled={busy}
                        onClick={()=>toggleVerify(d.id, d.isVerified)}
                        className={`px-2 py-1 rounded ${d.isVerified ? 'bg-green-600 text-white' : 'bg-gray-200'}`}
                    >
                      {d.isVerified ? 'Так' : 'Ні'}
                    </button>
                  </td>
                  <td className="p-2 border">
                    <button disabled={busy}
                            onClick={()=>removeDiploma(d.id)}
                            className="px-2 py-1 bg-red-600 text-white rounded">
                      Видалити
                    </button>
                  </td>
                </tr>
            ))}
            </tbody>
            <tfoot>
            <tr><td className="p-2 border" colSpan={7}>Всього: {total}</td></tr>
            </tfoot>
          </table>
        </div>

        {/* Блок інвайтів (виден тільки адміну, інакше API вернет 403 и список будет пуст) */}
        <div className="space-y-3">
          <h2 className="text-xl font-semibold">Інвайти</h2>
          <div className="flex gap-2 items-center">
            <input type="number" min="1" value={invMinutes} onChange={e=>setInvMinutes(e.target.value)}
                   className="border p-2 rounded w-24" />
            <span>хвилин</span>
            <button disabled={busy} onClick={addInvite} className="px-3 py-2 bg-blue-600 text-white rounded">Створити</button>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full text-sm border">
              <thead className="bg-gray-100">
              <tr>
                <th className="p-2 border">Код</th>
                <th className="p-2 border">Діє до</th>
                <th className="p-2 border">Дії</th>
              </tr>
              </thead>
              <tbody>
              {invites.length === 0 ? (
                  <tr><td colSpan={3} className="p-3">Немає активних інвайтів</td></tr>
              ) : invites.map(i => (
                  <tr key={i.id} className="odd:bg-white even:bg-gray-50">
                    <td className="p-2 border font-mono">{i.code}</td>
                    <td className="p-2 border">{i.expiresAt ? new Date(i.expiresAt).toLocaleString() : 'безстроково'}</td>
                    <td className="p-2 border space-x-2">
                      <button onClick={()=>copyInvite(i.code)} className="px-2 py-1 bg-gray-200 rounded">Копіювати</button>
                      <button disabled={busy} onClick={()=>revoke(i.id)} className="px-2 py-1 bg-red-600 text-white rounded">Відreзвати</button>
                    </td>
                  </tr>
              ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
  );
}
