import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createDiploma } from '../services/diplomaService';

export default function AddDiplomaPage() {
  const nav = useNavigate();
  const [form, setForm] = useState({
    studentName: '',
    faculty: '',
    specialty: '',
    year: '',
    diplomaNumber: '',
    file: null
  });
  const [progress, setProgress] = useState(0);
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState('');

  const onChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'file') setForm(f => ({ ...f, file: files?.[0] || null }));
    else setForm(f => ({ ...f, [name]: value }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setErr('');
    if (!form.file) { setErr('Додайте файл'); return; }
    setBusy(true);
    try {
      const fd = new FormData();
      fd.append('studentName', form.studentName.trim());
      if (form.faculty)   fd.append('faculty', form.faculty.trim());
      if (form.specialty) fd.append('specialty', form.specialty.trim());
      fd.append('year', String(form.year).trim());
      fd.append('diplomaNumber', form.diplomaNumber.trim());
      fd.append('file', form.file);

      const row = await createDiploma(fd, setProgress);
      alert('Диплом додано');
      nav(`/diplomas/${row.id}`);
    } catch (e) {
      setErr(e.response?.data?.message || 'Create error');
    } finally {
      setBusy(false);
    }
  };

  return (
      <div className="max-w-xl space-y-4">
        <h1 className="text-2xl font-bold">Додати диплом</h1>
        {err && <div className="text-red-600">{err}</div>}
        <form onSubmit={onSubmit} className="space-y-3">
          <input name="studentName" value={form.studentName} onChange={onChange}
                 placeholder="ПІБ" className="w-full border p-2 rounded" required/>
          <input name="faculty" value={form.faculty} onChange={onChange}
                 placeholder="Факультет" className="w-full border p-2 rounded"/>
          <input name="specialty" value={form.specialty} onChange={onChange}
                 placeholder="Спеціальність" className="w-full border p-2 rounded"/>
          <input name="year" type="number" min="1900" max="2100" value={form.year} onChange={onChange}
                 placeholder="Рік" className="w-full border p-2 rounded" required/>
          <input name="diplomaNumber" value={form.diplomaNumber} onChange={onChange}
                 placeholder="№ диплому" className="w-full border p-2 rounded" required/>
          <input name="file" type="file" accept="application/pdf,image/*"
                 onChange={onChange} className="w-full border p-2 rounded" required/>

          {progress > 0 && progress < 100 && (
              <div className="w-full bg-gray-200 rounded h-3">
                <div className="h-3 rounded bg-blue-600" style={{ width: `${progress}%` }}/>
              </div>
          )}

          <button disabled={busy} className="px-4 py-2 bg-blue-600 text-white rounded">
            {busy ? 'Завантаження…' : 'Зберегти'}
          </button>
        </form>
      </div>
  );
}
