import React from 'react';
import backgroundImage from '../assets/karazin_bg.jpg';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div
      className="bg-cover bg-center min-h-screen text-white"
      style={{
        backgroundImage: `url(${backgroundImage})`,
      }}
    >
      <div className="bg-black bg-opacity-60 min-h-screen p-8">
        <h1 className="text-4xl font-bold mb-4 text-center">Дипломи випускників Каразінського університету</h1>
        <p className="text-center mb-8">Оберіть спеціальність для перегляду доступних дипломів</p>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-4xl mx-auto">
          <Link to="/specialty/Комп’ютерна інженерія" className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500">
            Комп’ютерна інженерія
          </Link>
          <Link to="/specialty/Прикладна математика" className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500">
            Прикладна математика
          </Link>
          <Link to="/specialty/Фізика та астрономія" className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500">
            Фізика та астрономія
          </Link>
          <Link to="/specialty/Біологія" className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500">
            Біологія
          </Link>
          <Link to="/specialty/Психологія" className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500">
            Психологія
          </Link>
          <Link to="/specialty/Право" className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500">
            Право
          </Link>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
