// frontend/src/components/EditDiplomaModal.jsx
import { useEffect, useMemo, useState } from 'react';
import { getDiploma, updateDiploma, getFilters } from '../services/diplomaService';
import { apiUrl } from '../services/api';

export default function EditDiplomaModal({ id, onClose, onSaved }) {
    const [form, setForm] = useState(null);
    const [saving, setSaving] = useState(false);
    const [loading, setLoading] = useState(true);

    // довідники
    const [faculties, setFaculties] = useState([]);               // [{key,label}]
    const [specByFaculty, setSpecByFaculty] = useState({});       // {facultyKey: [{key,label}]}
    const [specToFaculty, setSpecToFaculty] = useState({});       // {specKey: facultyKey}
    const [allSpecs, setAllSpecs] = useState([]);                 // flat [{key,label}]

    // загрузка карточки + справочников
    useEffect(() => {
        let on = true;
        (async () => {
            setLoading(true);
            try {
                const [item, filters] = await Promise.all([
                    getDiploma(id),
                    getFilters().catch(() => ({})),
                ]);

                const fopts = filters.facultiesOptions || [];
                const sbyf  = filters.specialtyOptionsByFacultyKey || {};

                // сводная карта спец -> факультет и плоский список спец
                const stf = {};
                const all = [];
                const seen = new Set();
                Object.entries(sbyf).forEach(([fk, arr]) => {
                    (arr || []).forEach(s => {
                        stf[s.key] = fk;
                        if (!seen.has(s.key)) { seen.add(s.key); all.push({ key: s.key, label: s.label }); }
                    });
                });

                if (!on) return;

                setFaculties(fopts);
                setSpecByFaculty(sbyf);
                setSpecToFaculty(stf);
                setAllSpecs(all);

                // нормализация факультета/спец.
                const facultyValue   = item.facultyKey || item.faculty || item.facultyName || '';
                const specialtyValue = item.specialtyKey || item.specialty || '';

                setForm({
                    studentName:    item.studentName || '',
                    faculty:        String(facultyValue),
                    specialty:      String(specialtyValue),
                    year:           item.year || '',
                    diplomaNumber:  item.diplomaNumber || '',
                    isVerified:     !!item.isVerified,
                    notes:          item.notes || '',
                    fileUrl:        item.fileUrl || '',
                });
            } finally {
                if (on) setLoading(false);
            }
        })();
        return () => { on = false; };
    }, [id]);

    // список спецов под выбранный факультет
    const specOptions = useMemo(() => {
        if (!form) return [];
        if (form.faculty && specByFaculty[form.faculty]) return specByFaculty[form.faculty] || [];
        return allSpecs;
    }, [form, specByFaculty, allSpecs]);

    // если выбрали спец — подставить корректный faculty (когда известен из довідників)
    useEffect(() => {
        if (!form?.specialty) return;
        const fk = specToFaculty[form.specialty];
        if (fk && fk !== form.faculty) {
            setForm(prev => ({ ...prev, faculty: fk }));
        }
    }, [form?.specialty, specToFaculty, form?.faculty]);

    if (!id) return null;

    const onChange = (e) => {
        const { name, value, type, checked } = e.target;
        setForm(f => ({ ...f, [name]: type === 'checkbox' ? checked : value }));
    };

    const onSubmit = async () => {
        if (!form) return;
        setSaving(true);
        try {
            const payload = {
                studentName:   form.studentName,
                year:          form.year,
                diplomaNumber: form.diplomaNumber,
                isVerified:    !!form.isVerified,
                notes:         form.notes,
                fileUrl:       form.fileUrl,
            };
            if (form.faculty)   payload.faculty   = form.faculty;
            if (form.specialty) payload.specialty = form.specialty;

            await updateDiploma(id, payload);
            onSaved?.();
            onClose?.();
        } finally {
            setSaving(false);
        }
    };

    return (
        <div className="modal-backdrop" onClick={onClose}>
            <div className="modal" onClick={e => e.stopPropagation()} role="dialog" aria-modal="true" aria-labelledby="edit-title">
                <div className="modal-header">
                    <h3 id="edit-title">Редагувати диплом #{id}</h3>
                    <button onClick={onClose} aria-label="Закрити">×</button>
                </div>

                <div className="modal-body form" style={{ color: '#111' }}>
                    {loading || !form ? (
                        <div>Завантаження…</div>
                    ) : (
                        <>
                            <label>ПІБ
                                <input
                                    name="studentName"
                                    value={form.studentName}
                                    onChange={onChange}
                                    className="w-full border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                    placeholder="ПІБ випускника"
                                />
                            </label>

                            {/* Факультет */}
                            {faculties && faculties.length > 0 ? (
                                <label>Факультет
                                    <select
                                        name="faculty"
                                        value={form.faculty}
                                        onChange={onChange}
                                        className="w-full border p-2 rounded bg-white text-gray-900"
                                    >
                                        <option value="">— Оберіть факультет —</option>
                                        {faculties.map(f => (
                                            <option key={f.key} value={f.key}>{f.label}</option>
                                        ))}
                                    </select>
                                </label>
                            ) : (
                                <label>Факультет
                                    <input
                                        name="faculty"
                                        value={form.faculty}
                                        onChange={onChange}
                                        className="w-full border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                        placeholder="Назва факультету"
                                    />
                                </label>
                            )}

                            {/* Спеціальність */}
                            {specOptions && specOptions.length > 0 ? (
                                <label>Спеціальність
                                    <select
                                        name="specialty"
                                        value={form.specialty}
                                        onChange={onChange}
                                        className="w-full border p-2 rounded bg-white text-gray-900"
                                    >
                                        <option value="">— Оберіть спеціальність —</option>
                                        {specOptions.map(s => (
                                            <option key={s.key} value={s.key}>{s.label}</option>
                                        ))}
                                    </select>
                                </label>
                            ) : (
                                <label>Спеціальність
                                    <input
                                        name="specialty"
                                        value={form.specialty}
                                        onChange={onChange}
                                        className="w-full border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                        placeholder="Назва спеціальності"
                                    />
                                </label>
                            )}

                            <div className="grid grid-cols-2 gap-3">
                                <label>Рік
                                    <input
                                        name="year"
                                        value={form.year}
                                        onChange={onChange}
                                        className="w-full border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                        placeholder="Напр. 2023"
                                    />
                                </label>
                                <label>№ диплому
                                    <input
                                        name="diplomaNumber"
                                        value={form.diplomaNumber}
                                        onChange={onChange}
                                        className="w-full border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                        placeholder="Номер"
                                    />
                                </label>
                            </div>

                            <label className="inline-flex items-center gap-2 mt-1">
                                <input type="checkbox" name="isVerified" checked={form.isVerified} onChange={onChange} />
                                Підтверджено
                            </label>

                            <label>Нотатки
                                <textarea
                                    name="notes"
                                    value={form.notes}
                                    onChange={onChange}
                                    className="w-full border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                    rows={3}
                                    placeholder="Коментар"
                                />
                            </label>

                            <label>Файл (URL або /uploads/xxx)
                                <div className="flex gap-2">
                                    <input
                                        name="fileUrl"
                                        value={form.fileUrl}
                                        onChange={onChange}
                                        className="flex-1 border p-2 rounded bg-white text-gray-900 placeholder-gray-500"
                                        placeholder="/uploads/1756821296644_ivan.png"
                                    />
                                    {form.fileUrl ? (
                                        <a
                                            className="rounded border px-3 py-2 inline-flex items-center justify-center whitespace-nowrap"
                                            href={apiUrl(`/diplomas/${id}/file`)}
                                            target="_blank"
                                            rel="noreferrer"
                                        >
                                            Відкрити
                                        </a>
                                    ) : null}
                                </div>
                                <p className="text-xs text-gray-600 mt-1">
                                    Завантаження нового файлу робіть на сторінці «Додати». Тут можна змінити шлях або відкривати поточний файл.
                                </p>
                            </label>
                        </>
                    )}
                </div>

                <div className="modal-footer">
                    <button onClick={onClose} className="rounded border px-3 py-2">Скасувати</button>
                    <button onClick={onSubmit} disabled={saving || loading} className="rounded bg-blue-600 text-white px-4 py-2 ml-2">
                        {saving ? 'Збереження…' : 'Зберегти'}
                    </button>
                </div>
            </div>
        </div>
    );
}
