import React from 'react';
import backgroundImage from '../assets/karazin_bg.jpg';
import { Link } from 'react-router-dom';
import { FACULTIES } from '../data/catalog'; // ← твой каталог-объект

export default function HomePage() {
  // превращаем объект в массив пар [key, value]
  const facultyEntries = Object.entries(FACULTIES);

  return (
      <div
          className="bg-cover bg-center min-h-screen text-white"
          style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        {/* затемнение поверх фото */}
        <div className="bg-black bg-opacity-60 min-h-screen p-8">
          <h1 className="text-4xl font-bold mb-4 text-center">
            Дипломи випускників Каразінського університету
          </h1>
          <p className="text-center mb-8">
            Оберіть <span className="font-semibold">факультет</span> для перегляду доступних дипломів
          </p>

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-5xl mx-auto">
            {facultyEntries.map(([key, f]) => (
                <Link
                    key={key}
                    to={`/search?faculty=${encodeURIComponent(key)}`}
                    className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow
                         transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500"
                    title={f.name}
                >
                  {f.name}
                </Link>
            ))}
          </div>
        </div>
      </div>
  );
}
