import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getFacultiesTree } from '../services/diplomaService';

export default function HomePage() {
    const [rows, setRows] = useState([]);
    const [err, setErr] = useState('');

    useEffect(() => {
        (async () => {
            try {
                const { faculties } = await getFacultiesTree();
                setRows(faculties || []);
            } catch (e) {
                setErr(e?.response?.data?.message || 'Load error');
            }
        })();
    }, []);

    return (
        <div className="space-y-6">
            <h1 className="text-2xl font-bold">Факультети</h1>
            {err && <div className="text-red-600">{err}</div>}

            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3">
                {rows.map(({ faculty, count, specialties = [] }) => (
                    <div key={faculty} className="border rounded p-4 bg-white/70 hover:bg-white transition">
                        <div className="flex items-center justify-between">
                            <Link
                                to={`/search?faculty=${encodeURIComponent(faculty)}`}
                                className="font-semibold hover:underline"
                                title="Показати всі записи факультету"
                            >
                                {faculty}
                            </Link>
                            <span className="text-sm opacity-70">Записів: {count}</span>
                        </div>

                        <details className="mt-3">
                            <summary className="cursor-pointer select-none text-sm opacity-80">
                                Спеціальності ({specialties.length})
                            </summary>
                            <ul className="mt-2 space-y-1">
                                {specialties.map(s => (
                                    <li key={s.specialty} className="flex items-center justify-between">
                                        <Link
                                            to={`/search?faculty=${encodeURIComponent(faculty)}&specialty=${encodeURIComponent(s.specialty)}`}
                                            className="text-blue-700 hover:underline"
                                        >
                                            {s.specialty}
                                        </Link>
                                        <span className="text-xs opacity-60">{s.count}</span>
                                    </li>
                                ))}
                                {specialties.length === 0 && <li className="text-sm opacity-60">Немає спеціальностей</li>}
                            </ul>
                        </details>
                    </div>
                ))}

                {rows.length === 0 && <div>Немає даних</div>}
            </div>
        </div>
    );
}
