import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import { getDiplomas, updateDiploma, deleteDiploma } from '../services/diplomaService';
import EditDiplomaModal from '../components/EditDiplomaModal';
import { api } from '../services/api';

export default function AdminPage() {
  const [sp, setSp] = useSearchParams();
  const scope = sp.get('scope') || 'unverified';     // unverified | verified | all
  const [query, setQuery] = useState(sp.get('q') || '');
  const [sort, setSort]   = useState(sp.get('sort') || 'createdAt');   // createdAt | year | studentName | diplomaNumber
  const [dir, setDir]     = useState(sp.get('dir')  || 'desc');        // asc | desc

  const [diplomas, setDiplomas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(null);

  const [invites, setInvites] = useState([]);
  const refreshInvites = async () => {
     try {
          const r = await api.get('/invite/list'); // бекенд: /api/invite/list
           setInvites(r.data || []);
         } catch (e) {
           console.error(e);
         }
     };
  useEffect(() => { refreshInvites(); }, []);

  const genInvite = async () => {
       try {
           const r = await api.post('/invite/new'); // /api/invite/new
           setInvites(prev => [r.data, ...prev]);
         } catch (e) {
           console.error(e);
         }
     };
  const copyCode = async (code) => {
    try { await navigator.clipboard.writeText(code); } catch {}
  };
   const revoke = async (id) => {
      try {
           await api.delete(`/invite/${id}`); // /api/invite/:id
           setInvites(prev => prev.filter(i => i.id !== id));
         } catch (e) {
           console.error(e);
         }
     };

  const fetchData = async () => {
    try {
      setLoading(true);
      const filter =
          scope === 'unverified' ? { is_verified: 'false' } :
              scope === 'verified'   ? { is_verified: 'true' }  :
                  {};
      const data = await getDiplomas({
        ...filter,
        search: query || undefined,
        sort, dir,
        page: 1, limit: 50
      });
      setDiplomas(data.items || data);
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => { fetchData(); }, [scope, query, sort, dir]);

  useEffect(() => {
    const next = {};
    if (scope !== 'unverified') next.scope = scope;
    if (query) next.q = query;
    if (sort !== 'createdAt') next.sort = sort;
    if (dir  !== 'desc')      next.dir  = dir;
    setSp(next, { replace: true });
  }, [scope, query, sort, dir, setSp]);

  const toScope = (s) => setSp(s === 'unverified' ? {} : { scope: s }, { replace: true });

  const handleVerifyToggle = async (row) => {
    await updateDiploma(row.id, { isVerified: !row.isVerified });
    fetchData();
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Видалити цей диплом? Дію не можна скасувати.')) return;
    await deleteDiploma(id);
    fetchData();
  };

  if (loading) return <p>Завантаження...</p>;

  return (
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-semibold">Адмін-панель</h2>
          <div className="inline-flex rounded overflow-hidden border">
            <button
                onClick={() => toScope('unverified')}
                className={`px-3 py-1 ${scope === 'unverified' ? 'bg-blue-600 text-white' : 'bg-white'}`}
                title="Показати лише непідтверджені"
            >
              Непідтверджені
            </button>
            <button
                onClick={() => toScope('verified')}
                className={`px-3 py-1 border-l ${scope === 'verified' ? 'bg-blue-600 text-white' : 'bg-white'}`}
                title="Показати лише підтверджені"
            >
              Підтверджені
            </button>
            <button
                onClick={() => toScope('all')}
                className={`px-3 py-1 border-l ${scope === 'all' ? 'bg-blue-600 text-white' : 'bg-white'}`}
                title="Показати всі"
            >
              Усі
            </button>
          </div>
        </div>

        <div className="bg-white p-4 rounded shadow mb-4">
          <div className="flex items-center justify-between">
            <h3 className="font-semibold">Інвайт-коди (діють 10 хв)</h3>
            <button onClick={genInvite} className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700">
              Згенерувати
            </button>
          </div>
          <div className="mt-2 divide-y">
            {invites.length ? invites.map(i => (
                <div key={i.id} className="py-2 flex items-center justify-between">
                  <div className="font-mono text-sm">{i.code}</div>
                  <div className="text-sm text-gray-600">
                    до {new Date(i.expiresAt).toLocaleTimeString()}
                  </div>
                  <div className="flex gap-2">
                    <button onClick={() => copyCode(i.code)} className="px-2 py-1 border rounded">Копіювати</button>
                    <button onClick={() => revoke(i.id)} className="px-2 py-1 border rounded text-red-600">Скасувати</button>
                  </div>
                </div>
            )) : <p className="text-gray-500 py-2">Активних інвайтів немає.</p>}
          </div>
        </div>

        <div className="bg-white p-3 rounded shadow mb-3 flex flex-wrap items-end gap-3">
          <div>
            <label className="block text-sm mb-1">Пошук (ПІБ / №)</label>
            <input
                className="border rounded px-3 py-2"
                value={query}
                onChange={e => setQuery(e.target.value)}
                placeholder="Напр.: Іваненко або ABC123"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Сортувати за</label>
            <select
                className="border rounded px-3 py-2"
                value={sort}
                onChange={e => setSort(e.target.value)}
            >
              <option value="createdAt">Датою додавання</option>
              <option value="year">Роком</option>
              <option value="studentName">ПІБ</option>
              <option value="diplomaNumber">Номером диплому</option>
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Напрям</label>
            <select
                className="border rounded px-3 py-2"
                value={dir}
                onChange={e => setDir(e.target.value)}
            >
              <option value="desc">Спадання</option>
              <option value="asc">Зростання</option>
            </select>
          </div>
          <button onClick={fetchData} className="px-3 py-2 border rounded">Оновити</button>
        </div>

        <table className="min-w-full bg-white shadow rounded">
          <thead className="bg-gray-100">
          <tr>
            <th className="px-4 py-2 text-left">ПІБ</th>
            <th className="px-4 py-2 text-left">Спеціальність</th>
            <th className="px-4 py-2 text-left">Рік</th>
            <th className="px-4 py-2 text-left">Номер</th>
            <th className="px-4 py-2 text-left">Статус</th>
            <th className="px-4 py-2 text-left">Дії</th>
          </tr>
          </thead>
          <tbody>
          {diplomas.map(d => (
              <tr key={d.id} className="border-t">
                <td className="px-4 py-2">{d.studentName}</td>
                <td className="px-4 py-2">{d.specialty}</td>
                <td className="px-4 py-2">{d.year}</td>
                <td className="px-4 py-2">{d.diplomaNumber}</td>
                <td className="px-4 py-2">
                  {d.isVerified ? <span className="text-green-700">підтверджено</span> : <span className="text-yellow-700">очікує</span>}
                </td>
                <td className="px-4 py-2 space-x-2">
                  <button
                      onClick={() => setEditing(d)}
                      className="px-2 py-1 border rounded hover:bg-gray-50"
                  >
                    Редагувати
                  </button>
                  <button
                      onClick={() => handleVerifyToggle(d)}
                      className={`px-2 py-1 rounded ${d.isVerified ? 'bg-gray-600 text-white hover:bg-gray-700' : 'bg-green-600 text-white hover:bg-green-700'}`}
                      title={d.isVerified ? 'Зняти підтвердження' : 'Підтвердити'}
                  >
                    {d.isVerified ? 'Зняти підтвердження' : 'Підтвердити'}
                  </button>
                  <button
                      onClick={() => handleDelete(d.id)}
                      className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700"
                  >
                    Видалити
                  </button>
                </td>
              </tr>
          ))}
          {!diplomas.length && (
              <tr>
                <td colSpan="6" className="px-4 py-6 text-center text-gray-500">Немає записів</td>
              </tr>
          )}
          </tbody>
        </table>

        {editing && (
            <EditDiplomaModal
                open={!!editing}
                item={editing}
                onClose={() => setEditing(null)}
                onSaved={() => { setEditing(null); fetchData(); }}
            />
        )}
      </div>
  );
}
