// src/pages/RegisterPage.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { api } from '../services/api';

export default function RegisterPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const [invite, setInvite] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      await api.post('/register', { username, password, invite });
      navigate('/login');
    } catch (err) {
      const msg = err.response?.data?.message || 'Помилка реєстрації';
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
      <div className="max-w-md mx-auto bg-white shadow p-6 rounded">
        <h2 className="text-2xl font-semibold mb-4">Реєстрація користувача</h2>
        {error && <p className="text-red-600 mb-3">✖ {error}</p>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <input type="text" placeholder="email/логін"
                 value={email} onChange={e=>setEmail(e.target.value)}
                 required className="w-full border px-3 py-2 rounded"/>
          <input type="password" placeholder="пароль"
                 value={password} onChange={e=>setPassword(e.target.value)}
                 required className="w-full border px-3 py-2 rounded"/>
          <input
              placeholder="Invite code (якщо потрібно)"
              value={invite}
              onChange={e=>setInvite(e.target.value)}
              className="w-full border px-3 py-2 rounded"
          />
          <button disabled={loading}
                  className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50">
            {loading ? 'Зареєстровую...' : 'Зареєструватися'}
          </button>
        </form>
      </div>
  );
}
