import React, { useMemo } from 'react';

export default function FilterBar({
                                      q, setQ,
                                      scope, setScope,
                                      sort, setSort,
                                      dir, setDir,
                                      facultyKey, setFacultyKey,
                                      specialty, setSpecialty,
                                      facultiesOptions = [],
                                      specialtyOptions = [],
                                      allowUnverified = false,
                                      onRefresh = () => {},
                                  }) {
    const sortOptions = useMemo(
        () => ([
            { value: 'createdAt', label: 'За створенням' },
            { value: 'year',      label: 'За роком' },
            { value: 'studentName', label: 'ПІБ' },
            { value: 'diplomaNumber', label: '№ диплому' },
        ]),
        []
    );

    const dirOptions = useMemo(
        () => ([
            { value: 'desc', label: 'Спадання' },
            { value: 'asc',  label: 'Зростання' },
        ]),
        []
    );

    const onReset = () => {
        setQ('');
        setFacultyKey('');
        setSpecialty('');
        setSort('createdAt');
        setDir('desc');
        if (allowUnverified) setScope('all');
        onRefresh();
    };

    return (
        <div className="w-full grid gap-3 md:grid-cols-12 bg-white/60 p-3 rounded-xl shadow-sm">
            {/* Search */}
            <div className="md:col-span-4 col-span-12 flex items-stretch gap-2">
                <input
                    type="text"
                    value={q}
                    onChange={(e) => setQ(e.target.value)}
                    onKeyDown={(e) => { if (e.key === 'Enter') onRefresh(); }}
                    placeholder="Пошук (ПІБ або № диплому)…"
                    className="flex-1 rounded-md border border-gray-300 px-3 py-2"
                />
                <button
                    type="button"
                    onClick={onRefresh}
                    className="rounded-md bg-blue-600 text-white px-3 py-2"
                >
                    Знайти
                </button>
            </div>

            {/* Faculty */}
            <div className="md:col-span-2 col-span-12">
                <select
                    className="w-full rounded-md border border-gray-300 px-3 py-2"
                    value={facultyKey}
                    onChange={(e) => setFacultyKey(e.target.value)}
                >
                    <option value="">Усі факультети</option>
                    {facultiesOptions.map((f) => (
                        <option key={f.key} value={f.key}>{f.name}</option>
                    ))}
                </select>
            </div>

            {/* Specialty */}
            <div className="md:col-span-3 col-span-12">
                <select
                    className="w-full rounded-md border border-gray-300 px-3 py-2"
                    value={specialty}
                    onChange={(e) => setSpecialty(e.target.value)}
                >
                    <option value="">Усі спеціальності</option>
                    {specialtyOptions.map((s) => (
                        <option key={s.key} value={s.key}>{s.label}</option>
                    ))}
                </select>
            </div>

            {/* Scope (verified filter) */}
            <div className="md:col-span-3 col-span-12 flex gap-2">
                {allowUnverified && (
                    <select
                        className="flex-1 rounded-md border border-gray-300 px-3 py-2"
                        value={scope}
                        onChange={(e) => setScope(e.target.value)}
                    >
                        <option value="all">Всі</option>
                        <option value="verified">Лише підтверджені</option>
                        <option value="unverified">Лише непідтверджені</option>
                    </select>
                )}
            </div>

            {/* Sorting */}
            <div className="md:col-span-2 col-span-12">
                <select
                    className="w-full rounded-md border border-gray-300 px-3 py-2"
                    value={sort}
                    onChange={(e) => setSort(e.target.value)}
                >
                    {sortOptions.map(o => <option key={o.value} value={o.value}>{o.label}</option>)}
                </select>
            </div>
            <div className="md:col-span-2 col-span-12">
                <select
                    className="w-full rounded-md border border-gray-300 px-3 py-2"
                    value={dir}
                    onChange={(e) => setDir(e.target.value)}
                >
                    {dirOptions.map(o => <option key={o.value} value={o.value}>{o.label}</option>)}
                </select>
            </div>

            {/* Actions */}
            <div className="md:col-span-2 col-span-12 flex gap-2 justify-end">
                <button
                    type="button"
                    onClick={onRefresh}
                    className="rounded-md bg-sky-600 text-white px-3 py-2"
                >
                    Оновити
                </button>
                <button
                    type="button"
                    onClick={onReset}
                    className="rounded-md border border-gray-300 px-3 py-2"
                >
                    Скинути
                </button>
            </div>
        </div>
    );
}
