import React from 'react';

export default function FilterBar({
                                      q, setQ,
                                      scope, setScope,         // 'verified' | 'unverified' | 'all'
                                      sort, setSort,
                                      dir, setDir,
                                      facultyKey, setFacultyKey,          // ключ факультета
                                      specialty, setSpecialty,            // ключ специальности
                                      facultiesOptions = [],              // [{key,name}]
                                      specialtyOptions = [],              // [{key,label}] — либо все, либо факультета
                                      onRefresh,
                                      allowUnverified = false
                                  }) {
    return (
        <div className="flex flex-wrap gap-2 items-center">
            <input
                value={q}
                onChange={e=>setQ(e.target.value)}
                placeholder="Пошук (ПІБ/№/рік)"
                className="border p-2 rounded"
            />

            <select
                value={facultyKey}
                onChange={e=>{ setFacultyKey(e.target.value); /* сбрасываем спец-ть только если она чужая — это делает SearchPage */ }}
                className="border p-2 rounded"
            >
                <option value="">Усі факультети</option>
                {facultiesOptions.map(f => <option key={f.key} value={f.key}>{f.name}</option>)}
            </select>

            <select
                value={specialty}
                onChange={e=>setSpecialty(e.target.value)}
                className="border p-2 rounded"
                disabled={specialtyOptions.length === 0}   // блокируем только если действительно пусто
            >
                <option value="">{facultyKey ? 'Усі спеціальності факультету' : 'Усі спеціальності'}</option>
                {specialtyOptions.map(s => <option key={s.key} value={s.key}>{s.label}</option>)}
            </select>

            {allowUnverified && (
                <select value={scope} onChange={e=>setScope(e.target.value)} className="border p-2 rounded">
                    <option value="verified">Лише підтверджені</option>
                    <option value="unverified">Лише непідтверджені</option>
                    <option value="all">Усі</option>
                </select>
            )}

            <select value={sort} onChange={e=>setSort(e.target.value)} className="border p-2 rounded">
                <option value="createdAt">За створенням</option>
                <option value="year">Рік</option>
                <option value="studentName">ПІБ</option>
                <option value="diplomaNumber">№ диплому</option>
            </select>

            <select value={dir} onChange={e=>setDir(e.target.value)} className="border p-2 rounded">
                <option value="desc">↓</option>
                <option value="asc">↑</option>
            </select>

            <button onClick={onRefresh} className="px-3 py-2 bg-gray-200 rounded">Оновити</button>
        </div>
    );
}
