// frontend/src/components/DiplomaTable.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { fileUrl as abs } from '../services/api';

export default function DiplomaTable({
                                         items = [],
                                         total = 0,
                                         loading = false,
                                         error = '',
                                         busy = false,
                                         onVerify,
                                         onDelete,
                                         onEdit,              // (row) => void
                                         showActions = true
                                     }) {
    return (
        <div className="overflow-x-auto">
            {error && <div className="text-red-600 mb-2">{error}</div>}
            <table className="min-w-full text-sm border">
                <thead className="bg-gray-100">
                <tr>
                    <th className="p-2 border">ПІБ</th>
                    <th className="p-2 border">Спеціальність</th>
                    <th className="p-2 border">Факультет</th>
                    <th className="p-2 border">Рік</th>
                    <th className="p-2 border">№ диплому</th>
                    <th className="p-2 border">Файл</th>
                    <th className="p-2 border">Детально</th>
                    {showActions && <th className="p-2 border">Статус</th>}
                    {showActions && <th className="p-2 border">Дії</th>}
                </tr>
                </thead>
                <tbody>
                {loading ? (
                    <tr><td className="p-3" colSpan={showActions ? 9 : 7}>Завантаження…</td></tr>
                ) : items.length === 0 ? (
                    <tr><td className="p-3" colSpan={showActions ? 9 : 7}>Немає результатів</td></tr>
                ) : items.map(d => (
                    <tr key={d.id} className="odd:bg-white even:bg-gray-50">
                        <td className="p-2 border">{d.studentName}</td>
                        <td className="p-2 border">{d.specialty || '-'}</td>
                        <td className="p-2 border">{d.faculty || '-'}</td>
                        <td className="p-2 border">{d.year}</td>
                        <td className="p-2 border">{d.diplomaNumber}</td>
                        <td className="p-2 border">
                            {d.fileUrl ? (
                                <a className="text-blue-600 underline" href={abs(d.fileUrl)} target="_blank" rel="noreferrer">Відкрити</a>
                            ) : '-'}
                        </td>
                        <td className="p-2 border">
                            <Link className="text-blue-600 underline" to={`/diplomas/${d.id}`}>Переглянути</Link>
                        </td>
                        {showActions && (
                            <>
                                <td className="p-2 border">{d.isVerified ? 'Перевірено' : 'Не перевірено'}</td>
                                <td className="p-2 border space-x-2">
                                    {!d.isVerified && (
                                        <button disabled={busy} onClick={()=>onVerify?.(d.id, d.isVerified, true)}
                                                className="px-2 py-1 rounded bg-green-600 text-white">Підтвердити</button>
                                    )}
                                    <button disabled={busy} onClick={()=>onVerify?.(d.id, d.isVerified)}
                                            className="px-2 py-1 rounded bg-gray-200">
                                        {d.isVerified ? 'Зняти' : 'Позначити'}
                                    </button>
                                    <button disabled={busy} onClick={()=>onEdit?.(d)}
                                            className="px-2 py-1 rounded bg-blue-600 text-white">Редагувати</button>
                                    <button disabled={busy} onClick={()=>onDelete?.(d.id)}
                                            className="px-2 py-1 rounded bg-red-600 text-white">Видалити</button>
                                </td>
                            </>
                        )}
                    </tr>
                ))}
                </tbody>
                <tfoot>
                <tr><td className="p-2 border" colSpan={showActions ? 9 : 7}>Всього: {total}</td></tr>
                </tfoot>
            </table>
        </div>
    );
}
