import React, { useState } from 'react';
import { createDiploma } from '../services/diplomaService';

export default function AddDiplomaPage() {
  const [form, setForm] = useState({ studentName: '', specialty: '', year: '', diplomaNumber: '' });
  const [file, setFile] = useState(null);
  const [errors, setErrors] = useState({});
  const [msg, setMsg] = useState('');
  const [progress, setProgress] = useState(0);
  const [loading, setLoading] = useState(false);

  const onChange = (e) => setForm((s) => ({ ...s, [e.target.name]: e.target.value }));

  const onSubmit = async (e) => {
    e.preventDefault();
    setErrors({}); setMsg(''); setProgress(0); setLoading(true);
    try {
      const fd = new FormData();
      Object.entries(form).forEach(([k, v]) => fd.append(k, v));
      if (file) fd.append('file', file);
      await createDiploma(fd, setProgress);
      setMsg('Диплом додано. Очікує підтвердження.');
      setForm({ studentName: '', specialty: '', year: '', diplomaNumber: '' });
      setFile(null);
      setProgress(0);
    } catch (err) {
      const data = err.response?.data;
      setMsg(data?.message || 'Помилка завантаження');
      setErrors(data?.errors || {});
    } finally {
      setLoading(false);
    }
  };

  const err = (k) => errors?.[k] && <p className="text-red-600 text-sm mt-1">{errors[k]}</p>;

  return (
      <div className="max-w-xl mx-auto bg-white p-6 shadow rounded">
        <h2 className="text-2xl font-semibold mb-4">Додати диплом</h2>
        {msg && <div className="mb-3">{/✅/.test(msg) ? <p className="text-green-700">{msg}</p> : <p className="text-red-600">{msg}</p>}</div>}

        <form onSubmit={onSubmit} className="space-y-4">
          <div>
            <label className="block mb-1">ПІБ</label>
            <input name="studentName" value={form.studentName} onChange={onChange} required className="w-full border px-3 py-2 rounded"/>
            {err('studentName')}
          </div>

          <div>
            <label className="block mb-1">Спеціальність</label>
            <input name="specialty" value={form.specialty} onChange={onChange} required className="w-full border px-3 py-2 rounded"/>
            {err('specialty')}
          </div>

          <div>
            <label className="block mb-1">Рік</label>
            <input name="year" type="number" min="1900" max={new Date().getFullYear()} value={form.year} onChange={onChange} required className="w-full border px-3 py-2 rounded"/>
            {err('year')}
          </div>

          <div>
            <label className="block mb-1">Номер диплому</label>
            <input name="diplomaNumber" value={form.diplomaNumber} onChange={onChange} required className="w-full border px-3 py-2 rounded"/>
            {err('diplomaNumber')}
          </div>

          <div>
            <label className="block mb-1">Файл (PDF/JPG/PNG, ≤10 МБ)</label>
            <input type="file" accept=".pdf,.jpg,.jpeg,.png" onChange={(e) => setFile(e.target.files?.[0] || null)} />
            {err('file')}
          </div>

          {loading && (
              <div className="w-full bg-gray-200 rounded h-3">
                <div className="h-3 rounded bg-blue-600" style={{ width: `${progress}%` }} />
                <p className="text-sm mt-1">{progress}%</p>
              </div>
          )}

          <button disabled={loading} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50">
            {loading ? 'Завантаження…' : 'Завантажити диплом'}
          </button>
        </form>
      </div>
  );
}
