import { api } from './api';

export const getDiplomas = (params = {}) =>
    api.get('/diplomas', { params }).then(r => r.data);

export const getDiploma = (id) =>
    api.get(`/diplomas/${id}`).then(r => r.data);

export const createDiploma = (formData, onProgress) =>
    api.post('/diplomas', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        onUploadProgress: (e) =>
            onProgress?.(Math.round((e.loaded * 100) / (e.total || 1))),
    }).then(r => r.data);

export const updateDiploma = (id, data) =>
    api.put(`/diplomas/${id}`, data).then(r => r.data);

export const verifyDiploma = (id, isVerified) =>
    api.put(`/diplomas/${id}`, { isVerified }).then(r => r.data);

export const deleteDiploma = (id) =>
    api.delete(`/diplomas/${id}`).then(r => r.data);
