import React, { useEffect, useState } from 'react';
import { getFilters } from '../services/diplomaService';
import { Link } from 'react-router-dom';

export default function HomePage() {
  const [faculties, setFaculties] = useState([]);
  useEffect(() => {
    (async () => {
      const f = await getFilters();
      setFaculties(f.faculties || []);
    })();
  }, []);

  return (
      <div className="space-y-6">
        <h1 className="text-2xl font-bold">Факультети</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3">
          {faculties.map(f => (
              <Link key={f} to={`/search?faculty=${encodeURIComponent(f)}`}
                    className="border rounded p-4 bg-white/70 hover:bg-white transition">
                {f}
              </Link>
          ))}
          {faculties.length === 0 && <div>Немає даних</div>}
        </div>
      </div>
  );
}
