import React, { useState } from 'react';
import { createDiploma } from '../services/diplomaService';

export default function AddDiplomaPage() {
  const [form, setForm] = useState({
    studentName: '', specialty: '', year: '', diplomaNumber: '', file: null
  });
  const [message, setMessage] = useState('');
  const [progress, setProgress] = useState(0);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleFileChange = (e) => {
    const f = e.target.files[0];
    if (!f) return;
    const okType = ['application/pdf','image/jpeg'].includes(f.type);
    if (!okType) { setMessage('–î–æ–ø—É—Å—Ç–∏–º–æ –ª–∏—à–µ PDF –∞–±–æ JPG'); return; }
    if (f.size > 10 * 1024 * 1024) { setMessage('–§–∞–π–ª > 10 –ú–ë'); return; }
    setForm({ ...form, file: f });
    setMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(''); setProgress(0);
    const data = new FormData();
    data.append('studentName', form.studentName);
    data.append('specialty', form.specialty);
    data.append('year', form.year);
    data.append('diplomaNumber', form.diplomaNumber);
    data.append('file', form.file);

    try {
      await createDiploma(data, (evt) => {
        if (evt.total) setProgress(Math.round((evt.loaded * 100) / evt.total));
      });
      setMessage('‚úÖ –î–∏–ø–ª–æ–º –¥–æ–¥–∞–Ω–æ');
      setForm({ studentName:'', specialty:'', year:'', diplomaNumber:'', file:null });
      setProgress(0);
    } catch (err) {
      setMessage('‚ùå –ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è');
    }
  };

  return (
      <div className="max-w-xl mx-auto bg-white shadow p-6 rounded">
        <h2 className="text-2xl font-semibold mb-4">–î–æ–¥–∞—Ç–∏ –¥–∏–ø–ª–æ–º</h2>
        {message && <p className="mb-3">{message}</p>}
        {!!progress && <div className="mb-3 text-sm">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è: {progress}%</div>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <input name="studentName" required placeholder="–ü–Ü–ë" value={form.studentName} onChange={handleChange} className="w-full border px-3 py-2 rounded" />
          <input name="specialty" required placeholder="–°–ø–µ—Ü—ñ–∞–ª—å–Ω—ñ—Å—Ç—å" value={form.specialty} onChange={handleChange} className="w-full border px-3 py-2 rounded" />
          <input name="year" required type="number" placeholder="–†—ñ–∫" value={form.year} onChange={handleChange} className="w-full border px-3 py-2 rounded" />
          <input name="diplomaNumber" required placeholder="–ù–æ–º–µ—Ä –¥–∏–ø–ª–æ–º–∞" value={form.diplomaNumber} onChange={handleChange} className="w-full border px-3 py-2 rounded" />
          <input type="file" accept="application/pdf,image/jpeg" required onChange={handleFileChange} className="w-full" />
          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">üì§ –ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ –¥–∏–ø–ª–æ–º</button>
        </form>
      </div>
  );
}
