// invites
import api from './api';

export const listInvites = async () => {
    const { data } = await api.get('/api/admin/invites');
    return data.items;
};

export const createInvite = async (minutes = 10) => {
    const { data } = await api.post('/api/admin/invites', { expiresInMinutes: minutes });
    return data; // { id, code, createdAt, expiresAt }
};

export const revokeInvite = async (id) => {
    const { data } = await api.delete(`/api/admin/invites/${id}`);
    return data;
};
