import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getDiploma } from '../services/diplomaService';

const FILE_BASE = (process.env.REACT_APP_API_URL || 'http://localhost:5000/api').replace(/\/api$/, '');

export default function DiplomaDetailPage() {
    const { id } = useParams();
    const [diploma, setDiploma] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        getDiploma(id)
            .then(setDiploma)
            .catch((

            ) => setError('Не знайдено'));
    }, [id]);

    if (error) return <p className="text-red-600">{error}</p>;
    if (!diploma) return <p>Завантаження...</p>;

    return (
        <div className="max-w-5xl mx-auto space-y-6">
            <div className="bg-white shadow p-4 rounded">
                <h2 className="text-xl font-semibold mb-2">{diploma.studentName}</h2>
                <p><strong>Спеціальність:</strong> {diploma.specialty}</p>
                <p><strong>Рік:</strong> {diploma.year}</p>
                <p><strong>Номер:</strong> {diploma.diplomaNumber}</p>
                <a
                    className="inline-block mt-3 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                    href={`${FILE_BASE}${diploma.fileUrl}`}
                    target="_blank" rel="noreferrer"
                    download
                >
                    Завантажити
                </a>
            </div>
            <div className="bg-white shadow p-4 rounded">
                <iframe
                    src={`${FILE_BASE}${diploma.fileUrl}`}
                    title="PDF"
                    width="100%"
                    height="500px"
                />
            </div>
        </div>
    );
}
