import React, { useEffect, useRef, useState } from 'react';
import { updateDiploma, getFilters } from '../services/diplomaService';

const MAX_FILE_MB = Number(process.env.REACT_APP_MAX_FILE_MB || 10);
const ALLOWED_EXTS = ['pdf', 'png', 'jpg', 'jpeg'];

const extOf = (name = '') =>
    (String(name).toLowerCase().match(/\.([a-z0-9]+)(?:\?.*)?$/)?.[1] || '');

const isAllowedType = (file) =>
    !!file &&
    (ALLOWED_EXTS.includes(extOf(file.name)) ||
        file.type === 'application/pdf' ||
        file.type === 'image/png' ||
        file.type === 'image/jpeg');

const formatSize = (bytes) => `${(bytes / 1024 / 1024).toFixed(1)} МБ`;

const validateFile = (file) => {
    if (!file) return { ok: true };
    if (!isAllowedType(file)) return { ok: false, message: 'Дозволені лише PDF, PNG, JPG' };
    if (file.size > MAX_FILE_MB * 1024 * 1024) {
        return { ok: false, message: `Максимальний розмір ${MAX_FILE_MB} МБ (ваш ${formatSize(file.size)})` };
    }
    return { ok: true };
};

export default function EditDiplomaModal({ diploma, onClose, onSaved }) {
    const fileRef = useRef(null);

    const [opts, setOpts] = useState({
        facultiesOptions: [],                 // [{key,name}]
        specialtyOptionsByFacultyKey: {},     // { facKey: [{key,label}] }
    });

    const [form, setForm] = useState({
        id: diploma?.id || '',
        studentName: diploma?.studentName || '',
        facultyKey: '',                        // ключ факультета (для выбора специи)
        specialty: diploma?.specialty || '',   // ключ специальности (то, что хранится в БД)
        year: diploma?.year || '',
        diplomaNumber: diploma?.diplomaNumber || '',
        isVerified: Boolean(diploma?.isVerified),
    });

    const [file, setFile] = useState(null);
    const [err, setErr] = useState('');
    const [busy, setBusy] = useState(false);
    const [progress, setProgress] = useState(0);

    // загрузка довідників
    useEffect(() => {
        (async () => {
            try {
                const data = await getFilters();
                setOpts({
                    facultiesOptions: data.facultiesOptions || [],
                    specialtyOptionsByFacultyKey: data.specialtyOptionsByFacultyKey || {},
                });

                // если у записи есть specialty — можно попытаться подобрать facultyKey,
                // чтобы селект факультетов подсветить правильно
                const byKey = data.specialtyOptionsByFacultyKey || {};
                let foundFacKey = '';
                for (const [facKey, arr] of Object.entries(byKey)) {
                    if ((arr || []).some((s) => s.key === (diploma?.specialty || ''))) {
                        foundFacKey = facKey;
                        break;
                    }
                }
                setForm((prev) => ({ ...prev, facultyKey: foundFacKey }));
            } catch (e) {
                setErr(e?.response?.data?.message || 'Помилка завантаження довідників');
            }
        })();
    }, [diploma?.id]);

    // при смене записи сбрасываем локальные состояния
    useEffect(() => {
        setForm({
            id: diploma?.id || '',
            studentName: diploma?.studentName || '',
            facultyKey: '',                        // выставится автоподбором выше
            specialty: diploma?.specialty || '',
            year: diploma?.year || '',
            diplomaNumber: diploma?.diplomaNumber || '',
            isVerified: Boolean(diploma?.isVerified),
        });
        setFile(null);
        setErr('');
        setProgress(0);
    }, [diploma?.id, diploma?.studentName, diploma?.specialty, diploma?.year, diploma?.diplomaNumber, diploma?.isVerified]);

    const specList = form.facultyKey
        ? (opts.specialtyOptionsByFacultyKey[form.facultyKey] || [])
        : [];

    const onChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        if (name === 'file') {
            setFile(files?.[0] || null);
            return;
        }
        if (type === 'checkbox') {
            setForm((prev) => ({ ...prev, [name]: checked }));
            return;
        }
        setForm((prev) => ({ ...prev, [name]: value }));
        if (name === 'facultyKey') {
            setForm((prev) => ({ ...prev, specialty: '' }));
        }
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        setErr('');

        const vf = validateFile(file);
        if (!vf.ok) {
            setErr(vf.message);
            return;
        }

        try {
            setBusy(true);

            const fd = new FormData();
            fd.append('studentName', form.studentName.trim());
            fd.append('specialty', form.specialty); // отправляем ключ спец-ти
            fd.append('year', String(form.year));
            fd.append('diplomaNumber', form.diplomaNumber.trim());
            fd.append('isVerified', form.isVerified ? 'true' : 'false');
            if (file) fd.append('file', file);

            const saved = await updateDiploma(form.id, fd, (p) => setProgress(p));
            onSaved && onSaved(saved);
            onClose && onClose();
        } catch (e2) {
            setErr(e2?.response?.data?.message || 'Помилка збереження');
        } finally {
            setBusy(false);
            setProgress(0);
        }
    };

    if (!diploma) return null;

    return (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
            <div className="bg-white dark:bg-neutral-900 rounded-xl p-5 w-full max-w-xl">
                <div className="flex items-center justify-between mb-3">
                    <h2 className="text-xl font-semibold">Редагувати диплом</h2>
                    <button onClick={onClose} className="px-2 py-1 border rounded">×</button>
                </div>

                {err && <div className="mb-3 text-red-600">{err}</div>}

                <form onSubmit={onSubmit} className="space-y-3">
                    <input
                        name="studentName"
                        value={form.studentName}
                        onChange={onChange}
                        placeholder="ПІБ"
                        className="w-full border p-2 rounded"
                        required
                    />

                    <select
                        name="facultyKey"
                        value={form.facultyKey}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        required
                    >
                        <option value="">Оберіть факультет</option>
                        {opts.facultiesOptions.map((f) => (
                            <option key={f.key} value={f.key}>
                                {f.name}
                            </option>
                        ))}
                    </select>

                    <select
                        name="specialty"
                        value={form.specialty}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        required
                    >
                        <option value="">
                            {form.facultyKey ? 'Оберіть спеціальність' : 'Спочатку оберіть факультет'}
                        </option>
                        {specList.map((s) => (
                            <option key={s.key} value={s.key}>
                                {s.label}
                            </option>
                        ))}
                    </select>

                    <div className="flex gap-2">
                        <input
                            name="year"
                            type="number"
                            min="1900"
                            max="2100"
                            value={form.year}
                            onChange={onChange}
                            placeholder="Рік"
                            className="w-40 border p-2 rounded"
                            required
                        />
                        <input
                            name="diplomaNumber"
                            value={form.diplomaNumber}
                            onChange={onChange}
                            placeholder="№ диплому"
                            className="flex-1 border p-2 rounded"
                            required
                        />
                    </div>

                    <label className="inline-flex items-center gap-2">
                        <input
                            type="checkbox"
                            name="isVerified"
                            checked={form.isVerified}
                            onChange={onChange}
                        />
                        <span>Підтверджено</span>
                    </label>

                    <div className="space-y-1">
                        <input
                            ref={fileRef}
                            name="file"
                            type="file"
                            accept="application/pdf,image/png,image/jpeg"
                            onChange={onChange}
                            className="w-full border p-2 rounded"
                        />
                        <div className="text-xs opacity-70">
                            Дозволено: pdf, png, jpg. Максимум: {MAX_FILE_MB} МБ. (Не обовʼязково)
                        </div>
                    </div>

                    {progress > 0 && progress < 100 && (
                        <div className="w-full h-2 bg-gray-200 rounded">
                            <div className="h-2 rounded bg-blue-600" style={{ width: `${progress}%` }} />
                        </div>
                    )}

                    <div className="flex justify-end gap-2 pt-2">
                        <button type="button" onClick={onClose} className="px-4 py-2 border rounded">
                            Скасувати
                        </button>
                        <button disabled={busy} className="px-4 py-2 bg-blue-600 text-white rounded">
                            {busy ? 'Збереження…' : 'Зберегти'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
