import React, { useEffect, useMemo, useState } from 'react';
import { useSearchParams, useParams } from 'react-router-dom';
import { api } from '../services/api';
import { getDiplomas } from '../services/diplomaService';
import DiplomaTable from '../components/DiplomaTable';

export default function SearchPage() {
    const [sp, setSp] = useSearchParams();
    const { name: specialtyFromRoute } = useParams();

    const [catalog, setCatalog] = useState([]); // [{key, name, specialties:[{code,name}]}]
    const [catalogLoading, setCatalogLoading] = useState(true);

    const [faculty, setFaculty] = useState(sp.get('faculty') || '');
    const [specialtyCode, setSpecialtyCode] = useState(sp.get('specialtyCode') || '');

    const [query, setQuery] = useState(sp.get('name') || '');
    const [year, setYear]   = useState(sp.get('year') || '');
    const [page, setPage]   = useState(Number(sp.get('page') || 1));

    const [data, setData]   = useState({ items: [], total: 0, page: 1, pages: 1 });
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        let alive = true;
        api.get('/catalog/list')
            .then(r => { if (alive) setCatalog(r.data || []); })
            .finally(() => { if (alive) setCatalogLoading(false); });
        return () => { alive = false; };
    }, []);

    const specialties = useMemo(
        () => (catalog.find(f => f.key === faculty)?.specialties || []),
        [catalog, faculty]
    );

    const selectedSpecialtyName = useMemo(() => {
        if (specialtyCode) {
            const spc = specialties.find(s => s.code === specialtyCode);
            return spc?.name || '';
        }
        return specialtyFromRoute || sp.get('specialty') || '';
    }, [specialtyCode, specialties, specialtyFromRoute, sp]);

    useEffect(() => {
        const next = {};
        if (faculty)       next.faculty = faculty;
        if (specialtyCode) next.specialtyCode = specialtyCode;
        if (query)         next.name = query;
        if (year)          next.year = year;
        if (page > 1)      next.page = String(page);
        setSp(next, { replace: true });
    }, [faculty, specialtyCode, query, year, page, setSp]);

    useEffect(() => {
        const params = { page, limit: 10 };
        if (selectedSpecialtyName) params.specialty = selectedSpecialtyName;
        else if (faculty)          params.faculty   = faculty;
        if (query)                 params.search    = query;
        if (year)                  params.year      = year;

        setLoading(true);
        getDiplomas(params)
            .then((resp) => {
                setData(Array.isArray(resp)
                    ? { items: resp, total: resp.length, page, pages: 1 }
                    : resp);
            })
            .finally(() => setLoading(false));
    }, [faculty, selectedSpecialtyName, query, year, page]);

    const clearAll = () => {
        setFaculty('');
        setSpecialtyCode('');
        setQuery('');
        setYear('');
        setPage(1);
    };

    return (
        <div className="max-w-6xl mx-auto">
            <h2 className="text-2xl font-semibold mb-4">Пошук дипломів</h2>

            {/* Фільтр */}
            <div className="bg-white p-4 rounded shadow mb-4 flex flex-wrap gap-3">
                <div>
                    <label className="block text-sm mb-1">Факультет</label>
                    <select
                        className="border rounded px-3 py-2"
                        value={faculty}
                        onChange={(e) => { setFaculty(e.target.value); setSpecialtyCode(''); setPage(1); }}
                    >
                        <option value="">— Всі факультети —</option>
                        {catalog.map((f) => (
                            <option key={f.key} value={f.key}>{f.name}</option>
                        ))}
                    </select>
                </div>

                <div>
                    <label className="block text-sm mb-1">Спеціальність</label>
                    <select
                        className="border rounded px-3 py-2"
                        value={specialtyCode}
                        disabled={!faculty || catalogLoading}
                        onChange={(e) => { setSpecialtyCode(e.target.value); setPage(1); }}
                    >
                        <option value="">— Всі спеціальності факультету —</option>
                        {specialties.map(s => (
                            <option key={s.code} value={s.code}>{s.code} — {s.name}</option>
                        ))}
                    </select>
                </div>

                <div>
                    <label className="block text-sm mb-1">Пошук (ПІБ/№)</label>
                    <input
                        className="border rounded px-3 py-2"
                        value={query}
                        onChange={(e)=>{ setQuery(e.target.value); setPage(1); }}
                        placeholder="Прізвище або номер диплому"
                    />
                </div>

                <div>
                    <label className="block text-sm mb-1">Рік</label>
                    <input
                        type="number"
                        className="border rounded px-3 py-2"
                        min="1900"
                        max={new Date().getFullYear()}
                        value={year}
                        onChange={(e)=>{ setYear(e.target.value); setPage(1); }}
                    />
                </div>

                <button onClick={clearAll} className="px-3 py-2 border rounded">
                    Очистити
                </button>
            </div>

            {loading ? <p>Завантаження...</p> : <DiplomaTable diplomas={data.items || []} />}

            <div className="mt-4 flex gap-2">
                {Array.from({ length: data.pages || 1 }, (_, i) => i + 1).map(p => (
                    <button
                        key={p}
                        onClick={() => setPage(p)}
                        className={`px-3 py-1 rounded border ${p === (data.page || 1) ? 'bg-blue-600 text-white' : 'bg-white'}`}
                    >
                        {p}
                    </button>
                ))}
            </div>
        </div>
    );
}
