import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import { getDiplomas, updateDiploma, deleteDiploma } from '../services/diplomaService';
import EditDiplomaModal from '../components/EditDiplomaModal';
import { listInvites, createInvite, revokeInvite } from '../services/inviteService';

export default function AdminPage() {
  const [sp, setSp] = useSearchParams();
  const scope = sp.get('scope') || 'unverified';

  const [diplomas, setDiplomas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      const filter =
          scope === 'unverified' ? { is_verified: 'false' } :
              scope === 'verified'   ? { is_verified: 'true' }  :
                  {};
      const data = await getDiplomas({ ...filter, page: 1, limit: 50 });
      setDiplomas(data.items || data);
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchData(); }, [scope]);

  const toScope = (s) => setSp(s === 'unverified' ? {} : { scope: s }, { replace: true });

  const handleVerifyToggle = async (row) => {
    await updateDiploma(row.id, { isVerified: !row.isVerified });
    fetchData();
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Видалити цей диплом? Дію не можна скасувати.')) return;
    await deleteDiploma(id);
    fetchData();
  };
  const [invites, setInvites] = useState([]);
  const refreshInvites = async () => setInvites(await listInvites());
  useEffect(() => { refreshInvites(); }, []);

  const genInvite = async () => {
    const row = await createInvite();
    setInvites(prev => [row, ...prev]);
  };
  const copyCode = async (code) => {
    try { await navigator.clipboard.writeText(code); } catch {}
  };
  const revoke = async (id) => {
    await revokeInvite(id);
    setInvites(prev => prev.filter(i => i.id !== id));
  };
  if (loading) return <p>Завантаження...</p>;

  return (
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-semibold">Адмін-панель</h2>
          <div className="inline-flex rounded overflow-hidden border">
            <button
                onClick={() => toScope('unverified')}
                className={`px-3 py-1 ${scope === 'unverified' ? 'bg-blue-600 text-white' : 'bg-white'}`}
                title="Показати лише непідтверджені"
            >
              Непідтверджені
            </button>
            <button
                onClick={() => toScope('verified')}
                className={`px-3 py-1 border-l ${scope === 'verified' ? 'bg-blue-600 text-white' : 'bg-white'}`}
                title="Показати лише підтверджені"
            >
              Підтверджені
            </button>
            <button
                onClick={() => toScope('all')}
                className={`px-3 py-1 border-l ${scope === 'all' ? 'bg-blue-600 text-white' : 'bg-white'}`}
                title="Показати всі"
            >
              Усі
            </button>
          </div>
        </div>

        <table className="min-w-full bg-white shadow rounded">
          <thead className="bg-gray-100">
          <tr>
            <th className="px-4 py-2 text-left">ПІБ</th>
            <th className="px-4 py-2 text-left">Спеціальність</th>
            <th className="px-4 py-2 text-left">Рік</th>
            <th className="px-4 py-2 text-left">Номер</th>
            <th className="px-4 py-2 text-left">Статус</th>
            <th className="px-4 py-2 text-left">Дії</th>
          </tr>
          </thead>
          <tbody>
          {diplomas.map(d => (
              <tr key={d.id} className="border-t">
                <td className="px-4 py-2">{d.studentName}</td>
                <td className="px-4 py-2">{d.specialty}</td>
                <td className="px-4 py-2">{d.year}</td>
                <td className="px-4 py-2">{d.diplomaNumber}</td>
                <td className="px-4 py-2">
                  {d.isVerified ? <span className="text-green-700">підтверджено</span> : <span className="text-yellow-700">очікує</span>}
                </td>
                <td className="px-4 py-2 space-x-2">
                  <button
                      onClick={() => setEditing(d)}
                      className="px-2 py-1 border rounded hover:bg-gray-50"
                  >
                    Редагувати
                  </button>

                  <button
                      onClick={() => handleVerifyToggle(d)}
                      className={`px-2 py-1 rounded ${d.isVerified ? 'bg-gray-600 text-white hover:bg-gray-700' : 'bg-green-600 text-white hover:bg-green-700'}`}
                      title={d.isVerified ? 'Зняти підтвердження' : 'Підтвердити'}
                  >
                    {d.isVerified ? 'Зняти підтвердження' : 'Підтвердити'}
                  </button>

                  <button
                      onClick={() => handleDelete(d.id)}
                      className="bg-red-600 text-white px-2 py-1 rounded hover:bg-red-700"
                  >
                    Видалити
                  </button>
                </td>
              </tr>
          ))}
          {!diplomas.length && (
              <tr>
                <td colSpan="6" className="px-4 py-6 text-center text-gray-500">Немає записів</td>
              </tr>
          )}
          </tbody>
        </table>

        {editing && (
            <EditDiplomaModal
                open={!!editing}
                item={editing}
                onClose={() => setEditing(null)}
                onSaved={() => { setEditing(null); fetchData(); }}
            />
        )}
      </div>
  );
}
