import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createDiploma, getFilters } from '../services/diplomaService';

// Лимит берём из REACT_APP_MAX_FILE_MB, иначе 10
const MAX_FILE_MB = Number(process.env.REACT_APP_MAX_FILE_MB || 10);
const ALLOWED_EXTS = ['pdf', 'png', 'jpg', 'jpeg'];

const extOf = (name = '') => (String(name).toLowerCase().match(/\.([a-z0-9]+)(?:\?.*)?$/)?.[1] || '');
const isAllowedType = (file) =>
    !!file && (
        ALLOWED_EXTS.includes(extOf(file.name)) ||
        file.type === 'application/pdf' ||
        file.type === 'image/png' ||
        file.type === 'image/jpeg'
    );
const formatSize = (bytes) => `${(bytes / 1024 / 1024).toFixed(1)} МБ`;
const validateFile = (file) => {
  if (!file) return { ok: false, message: 'Файл не обрано' };
  if (!isAllowedType(file)) return { ok: false, message: 'Дозволені лише PDF, PNG, JPG' };
  if (file.size > MAX_FILE_MB * 1024 * 1024) {
    return { ok: false, message: `Максимальний розмір ${MAX_FILE_MB} МБ (ваш ${formatSize(file.size)})` };
  }
  return { ok: true };
};

export default function AddDiplomaPage() {
  const nav = useNavigate();
  const [opts, setOpts] = useState({ faculties: [], specialtiesByFaculty: {} });
  const [form, setForm] = useState({ studentName:'', faculty:'', specialty:'', year:'', diplomaNumber:'', file:null });
  const [progress, setProgress] = useState(0);
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState('');
  const fileRef = useRef(null);

  useEffect(() => { (async () => {
    try { const { faculties, specialtiesByFaculty } = await getFilters(); setOpts({ faculties, specialtiesByFaculty }); } catch {}
  })(); }, []);

  const onChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'file') {
      const f = files?.[0] || null;
      const v = validateFile(f);
      if (!v.ok) { setErr(v.message); setForm(s => ({...s, file:null})); if (fileRef.current) fileRef.current.value=''; return; }
      setErr(''); setForm(s => ({...s, file:f})); return;
    }
    if (name === 'faculty') { setForm(s => ({...s, faculty:value, specialty:''})); return; }
    setForm(s => ({...s, [name]:value}));
  };

  const onSubmit = async (e) => {
    e.preventDefault(); setErr('');
    const v = validateFile(form.file); if (!v.ok) return setErr(v.message);
    if (!form.specialty) return setErr('Оберіть спеціальність');

    setBusy(true);
    try {
      const fd = new FormData();
      fd.append('studentName', form.studentName.trim());
      fd.append('faculty', form.faculty.trim());
      fd.append('specialty', form.specialty.trim());
      fd.append('year', String(form.year).trim());
      fd.append('diplomaNumber', form.diplomaNumber.trim());
      fd.append('file', form.file);
      const row = await createDiploma(fd, setProgress);
      alert('Диплом додано'); nav(`/diplomas/${row.id}`);
    } catch (e2) { setErr(e2.response?.data?.message || 'Create error'); }
    finally { setBusy(false); }
  };

  const specList = form.faculty ? (opts.specialtiesByFaculty[form.faculty] || []) : [];

  return (
      <div className="max-w-xl space-y-4">
        <h1 className="text-2xl font-bold">Додати диплом</h1>
        {err && <div className="text-red-600">{err}</div>}
        <form onSubmit={onSubmit} className="space-y-3">
          <input name="studentName" value={form.studentName} onChange={onChange} placeholder="ПІБ" className="w-full border p-2 rounded" required/>
          <select name="faculty" value={form.faculty} onChange={onChange} className="w-full border p-2 rounded" required>
            <option value="">Оберіть факультет</option>
            {opts.faculties.map(f => <option key={f} value={f}>{f}</option>)}
          </select>
          <select name="specialty" value={form.specialty} onChange={onChange} className="w-full border p-2 rounded" required>
            <option value="">Оберіть спеціальність</option>
            {specList.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
          <input name="year" type="number" min="1900" max="2100" value={form.year} onChange={onChange} placeholder="Рік" className="w-full border p-2 rounded" required/>
          <input name="diplomaNumber" value={form.diplomaNumber} onChange={onChange} placeholder="№ диплому" className="w-full border p-2 rounded" required/>
          <input ref={fileRef} name="file" type="file" accept="application/pdf,image/png,image/jpeg" onChange={onChange} className="w-full border p-2 rounded" required/>
          <div className="text-xs opacity-70">Дозволено: pdf, png, jpg. Максимум: {MAX_FILE_MB} МБ.</div>
          {progress > 0 && progress < 100 && (<div className="w-full bg-gray-200 rounded h-3"><div className="h-3 rounded bg-blue-600" style={{width:`${progress}%`}}/></div>)}
          <button disabled={busy} className="px-4 py-2 bg-blue-600 text-white rounded">{busy ? 'Завантаження…' : 'Зберегти'}</button>
        </form>
      </div>
  );
}
