// frontend/src/components/EditDiplomaModal.jsx
import React, { useEffect, useRef, useState } from 'react';
import { updateDiploma, updateDiplomaMultipart, getFilters } from '../services/diplomaService';

// Лимит берём из REACT_APP_MAX_FILE_MB, иначе 10 МБ
const MAX_FILE_MB = Number(process.env.REACT_APP_MAX_FILE_MB || 10);
const ALLOWED_EXTS = ['pdf', 'png', 'jpg', 'jpeg'];

const extOf = (n = '') =>
    (String(n).toLowerCase().match(/\.([a-z0-9]+)(?:\?.*)?$/)?.[1] || '');

const isAllowedType = (f) =>
    !!f &&
    (ALLOWED_EXTS.includes(extOf(f.name)) ||
        f.type === 'application/pdf' ||
        f.type === 'image/png' ||
        f.type === 'image/jpeg');

const formatSize = (bytes) => `${(bytes / 1024 / 1024).toFixed(1)} МБ`;

const validateFile = (f) => {
    if (!f) return { ok: false, message: 'Файл не обрано' };
    if (!isAllowedType(f)) return { ok: false, message: 'Дозволені лише PDF, PNG, JPG' };
    if (f.size > MAX_FILE_MB * 1024 * 1024) {
        return { ok: false, message: `Максимальний розмір ${MAX_FILE_MB} МБ (ваш ${formatSize(f.size)})` };
    }
    return { ok: true };
};

export default function EditDiplomaModal({ open, onClose, row, onSaved }) {
    const [opts, setOpts] = useState({ faculties: [], specialtiesByFaculty: {} });
    const [form, setForm] = useState(null);
    const [file, setFile] = useState(null);
    const [busy, setBusy] = useState(false);
    const [err, setErr] = useState('');
    const fileRef = useRef(null);

    useEffect(() => {
        (async () => {
            try {
                const { faculties, specialtiesByFaculty } = await getFilters();
                setOpts({ faculties, specialtiesByFaculty });
            } catch {}
        })();
    }, []);

    useEffect(() => {
        if (open && row) {
            setForm({
                studentName: row.studentName || '',
                faculty: row.faculty || '',
                specialty: row.specialty || '',
                year: row.year || '',
                diplomaNumber: row.diplomaNumber || '',
                isVerified: !!row.isVerified,
            });
            setFile(null);
            setErr('');
            if (fileRef.current) fileRef.current.value = '';
        }
    }, [open, row]);

    if (!open || !form) return null;

    const onChange = (e) => {
        const { name, value, type, checked, files } = e.target;

        if (name === 'file') {
            const f = files?.[0] || null;
            if (!f) { setFile(null); return; }
            const v = validateFile(f);
            if (!v.ok) {
                setErr(v.message);
                setFile(null);
                if (fileRef.current) fileRef.current.value = '';
                return;
            }
            setErr('');
            setFile(f);
            return;
        }

        if (name === 'faculty') {
            setForm((p) => ({ ...p, faculty: value, specialty: '' }));
            return;
        }

        setForm((p) => ({ ...p, [name]: type === 'checkbox' ? checked : value }));
    };

    const save = async (e) => {
        e.preventDefault();
        setBusy(true);
        setErr('');

        try {
            if (!form.specialty) throw new Error('Оберіть спеціальність');

            if (file) {
                const v = validateFile(file);
                if (!v.ok) throw new Error(v.message);

                const fd = new FormData();
                Object.entries(form).forEach(([k, v2]) => fd.append(k, v2));
                fd.append('file', file);
                await updateDiplomaMultipart(row.id, fd);
            } else {
                await updateDiploma(row.id, form);
            }

            onSaved?.();
            onClose?.();
        } catch (e2) {
            setErr(e2.response?.data?.message || e2.message || 'Save error');
        } finally {
            setBusy(false);
        }
    };

    const specList = form.faculty ? (opts.specialtiesByFaculty[form.faculty] || []) : [];

    return (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded p-4 w-full max-w-xl space-y-3">
                <div className="text-lg font-semibold">Редагувати</div>
                {err && <div className="text-red-600">{err}</div>}

                <form onSubmit={save} className="space-y-2">
                    <input
                        name="studentName"
                        value={form.studentName}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        placeholder="ПІБ"
                    />

                    <select
                        name="faculty"
                        value={form.faculty}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        required
                    >
                        <option value="">Оберіть факультет</option>
                        {opts.faculties.map((f) => (
                            <option key={f} value={f}>{f}</option>
                        ))}
                    </select>

                    <select
                        name="specialty"
                        value={form.specialty}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        required
                    >
                        <option value="">Оберіть спеціальність</option>
                        {specList.map((s) => (
                            <option key={s} value={s}>{s}</option>
                        ))}
                    </select>

                    <input
                        name="year"
                        type="number"
                        value={form.year}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        placeholder="Рік"
                    />

                    <input
                        name="diplomaNumber"
                        value={form.diplomaNumber}
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                        placeholder="№ диплому"
                    />

                    <label className="flex items-center gap-2">
                        <input
                            type="checkbox"
                            name="isVerified"
                            checked={form.isVerified}
                            onChange={onChange}
                        />
                        <span>Підтверджено</span>
                    </label>

                    <input
                        ref={fileRef}
                        name="file"
                        type="file"
                        accept="application/pdf,image/png,image/jpeg"
                        onChange={onChange}
                        className="w-full border p-2 rounded"
                    />
                    <div className="text-xs opacity-70">
                        Дозволено: pdf, png, jpg. Максимум: {MAX_FILE_MB} МБ.
                    </div>

                    <div className="flex gap-2 justify-end pt-2">
                        <button type="button" onClick={onClose} className="px-3 py-2 rounded border">
                            Скасувати
                        </button>
                        <button disabled={busy} className="px-3 py-2 rounded bg-blue-600 text-white">
                            {busy ? 'Збереження…' : 'Зберегти'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
