import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createDiploma, getFilters } from '../services/diplomaService';

// Настройки проверки
const MAX_FILE_MB = 20; // максимум, легко поменять
const ALLOWED_EXTS = ['pdf', 'png', 'jpg', 'jpeg', 'webp', 'gif', 'bmp', 'tiff', 'svg'];

function extOf(name = '') {
  const m = String(name).toLowerCase().match(/\.([a-z0-9]+)(?:\?.*)?$/i);
  return m ? m[1] : '';
}
function isAllowedType(file) {
  if (!file) return false;
  const e = extOf(file.name);
  if (ALLOWED_EXTS.includes(e)) return true;
  // fallback по MIME
  if (file.type === 'application/pdf') return true;
  if (file.type.startsWith('image/')) return true;
  return false;
}
function formatSize(bytes) {
  const mb = bytes / (1024 * 1024);
  return `${mb.toFixed(mb < 10 ? 1 : 0)} МБ`;
}
function validateFile(file) {
  if (!file) return { ok: false, message: 'Файл не обрано' };
  if (!isAllowedType(file)) {
    return {
      ok: false,
      message: `Неприпустимий тип файлу. Дозволено: ${ALLOWED_EXTS.join(', ')}`,
    };
  }
  const maxBytes = MAX_FILE_MB * 1024 * 1024;
  if (file.size > maxBytes) {
    return {
      ok: false,
      message: `Файл завеликий (${formatSize(file.size)}). Максимум ${MAX_FILE_MB} МБ`,
    };
  }
  return { ok: true };
}

export default function AddDiplomaPage() {
  const nav = useNavigate();
  const [opts, setOpts] = useState({ faculties: [], specialtiesByFaculty: {} });

  const [form, setForm] = useState({
    studentName: '',
    faculty: '',
    specialty: '',
    year: '',
    diplomaNumber: '',
    file: null,
  });

  const [progress, setProgress] = useState(0);
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState('');
  const fileRef = useRef(null);

  useEffect(() => {
    (async () => {
      try {
        const { faculties, specialtiesByFaculty } = await getFilters();
        setOpts({ faculties, specialtiesByFaculty });
      } catch {}
    })();
  }, []);

  const onChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'file') {
      const file = files?.[0] || null;
      const v = validateFile(file);
      if (!v.ok) {
        setErr(v.message);
        setForm((f) => ({ ...f, file: null }));
        if (fileRef.current) fileRef.current.value = ''; // чистим инпут
        return;
      }
      setErr('');
      setForm((f) => ({ ...f, file }));
      return;
    }
    if (name === 'faculty') {
      setForm((f) => ({ ...f, faculty: value, specialty: '' }));
      return;
    }
    setForm((f) => ({ ...f, [name]: value }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setErr('');

    // финальная проверка на всякий случай
    const v = validateFile(form.file);
    if (!v.ok) {
      setErr(v.message);
      return;
    }
    if (!form.specialty) {
      setErr('Оберіть спеціальність');
      return;
    }

    setBusy(true);
    try {
      const fd = new FormData();
      fd.append('studentName', form.studentName.trim());
      fd.append('faculty', form.faculty.trim());
      fd.append('specialty', form.specialty.trim());
      fd.append('year', String(form.year).trim());
      fd.append('diplomaNumber', form.diplomaNumber.trim());
      fd.append('file', form.file);

      const row = await createDiploma(fd, setProgress);
      alert('Диплом додано');
      nav(`/diplomas/${row.id}`);
    } catch (e2) {
      setErr(e2.response?.data?.message || 'Create error');
    } finally {
      setBusy(false);
    }
  };

  const specList = form.faculty ? opts.specialtiesByFaculty[form.faculty] || [] : [];

  return (
      <div className="max-w-xl space-y-4">
        <h1 className="text-2xl font-bold">Додати диплом</h1>
        {err && <div className="text-red-600">{err}</div>}

        <form onSubmit={onSubmit} className="space-y-3">
          <input
              name="studentName"
              value={form.studentName}
              onChange={onChange}
              placeholder="ПІБ"
              className="w-full border p-2 rounded"
              required
          />

          <select
              name="faculty"
              value={form.faculty}
              onChange={onChange}
              className="w-full border p-2 rounded"
              required
          >
            <option value="">Оберіть факультет</option>
            {opts.faculties.map((f) => (
                <option key={f} value={f}>
                  {f}
                </option>
            ))}
          </select>

          <select
              name="specialty"
              value={form.specialty}
              onChange={onChange}
              className="w-full border p-2 rounded"
              required
          >
            <option value="">Оберіть спеціальність</option>
            {specList.map((s) => (
                <option key={s} value={s}>
                  {s}
                </option>
            ))}
          </select>

          <input
              name="year"
              type="number"
              min="1900"
              max="2100"
              value={form.year}
              onChange={onChange}
              placeholder="Рік"
              className="w-full border p-2 rounded"
              required
          />

          <input
              name="diplomaNumber"
              value={form.diplomaNumber}
              onChange={onChange}
              placeholder="№ диплому"
              className="w-full border p-2 rounded"
              required
          />

          <input
              ref={fileRef}
              name="file"
              type="file"
              accept="application/pdf,image/*"
              onChange={onChange}
              className="w-full border p-2 rounded"
              required
          />
          <div className="text-xs opacity-70">
            Дозволено: {ALLOWED_EXTS.join(', ')}. Максимум: {MAX_FILE_MB} МБ.
          </div>

          {progress > 0 && progress < 100 && (
              <div className="w-full bg-gray-200 rounded h-3">
                <div className="h-3 rounded bg-blue-600" style={{ width: `${progress}%` }} />
              </div>
          )}

          <button disabled={busy} className="px-4 py-2 bg-blue-600 text-white rounded">
            {busy ? 'Завантаження…' : 'Зберегти'}
          </button>
        </form>
      </div>
  );
}
