import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createDiploma, getFilters } from '../services/diplomaService';

const MAX_FILE_MB = Number(process.env.REACT_APP_MAX_FILE_MB || 10);
const ALLOWED_EXTS = ['pdf', 'png', 'jpg', 'jpeg'];

const extOf = (name = '') => (String(name).toLowerCase().match(/\.([a-z0-9]+)(?:\?.*)?$/)?.[1] || '');
const isAllowedType = (file) =>
    !!file && (ALLOWED_EXTS.includes(extOf(file.name)) || file.type === 'application/pdf' || file.type === 'image/png' || file.type === 'image/jpeg');
const formatSize = (bytes) => `${(bytes / 1024 / 1024).toFixed(1)} МБ`;
const validateFile = (file) => {
  if (!file) return { ok: false, message: 'Файл не обрано' };
  if (!isAllowedType(file)) return { ok: false, message: 'Дозволені лише PDF, PNG, JPG' };
  if (file.size > MAX_FILE_MB * 1024 * 1024) return { ok: false, message: `Максимальний розмір ${MAX_FILE_MB} МБ (ваш ${formatSize(file.size)})` };
  return { ok: true };
};

export default function AddDiplomaPage() {
  const nav = useNavigate();
  const fileRef = useRef(null);

  // Довідники по ключам (унифицировано)
  const [opts, setOpts] = useState({
    facultiesOptions: [],                 // [{key,name}]
    specialtyOptionsByFacultyKey: {},     // { facKey: [{key,label}] }
  });

  const [form, setForm] = useState({
    studentName: '',
    facultyKey: '',
    specialty: '',
    year: '',
    diplomaNumber: '',
  });

  const [file, setFile] = useState(null);
  const [err, setErr] = useState('');
  const [busy, setBusy] = useState(false);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    (async () => {
      try {
        const data = await getFilters();
        setOpts({
          facultiesOptions: data.facultiesOptions || [],
          specialtyOptionsByFacultyKey: data.specialtyOptionsByFacultyKey || {},
        });
      } catch (e) {
        setErr(e?.response?.data?.message || 'Помилка завантаження довідників');
      }
    })();
  }, []);

  const specList = form.facultyKey ? (opts.specialtyOptionsByFacultyKey[form.facultyKey] || []) : [];

  const onChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'file') { setFile(files?.[0] || null); return; }
    setForm(prev => ({ ...prev, [name]: value }));
    if (name === 'facultyKey') setForm(prev => ({ ...prev, specialty: '' }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setErr('');

    // берём файл из state или напрямую из ref (на случай, если onChange не сработал)
    const realFile = file || fileRef.current?.files?.[0] || null;
    const vf = validateFile(realFile);
    if (!vf.ok) { setErr(vf.message); return; }

    try {
      setBusy(true);
      const fd = new FormData();
      fd.append('studentName', form.studentName.trim());
      fd.append('specialty', form.specialty);               // БД хранит ключ спец-ти
      fd.append('year', String(form.year));
      fd.append('diplomaNumber', form.diplomaNumber.trim());
      fd.append('file', realFile);                          // поле должно называться 'file'

      const saved = await createDiploma(fd, (p) => setProgress(p));
      nav(`/diplomas/${saved?.id || ''}`);
    } catch (e2) {
      setErr(e2?.response?.data?.message || 'Помилка збереження');
    } finally {
      setBusy(false);
      setProgress(0);
    }
  };

  return (
      <div className="container mx-auto p-4 max-w-2xl">
        <h1 className="text-2xl font-bold mb-4">Додати диплом</h1>
        {err && <div className="mb-3 text-red-600">{err}</div>}

        <form onSubmit={onSubmit} className="space-y-3">
          <input name="studentName" value={form.studentName} onChange={onChange} placeholder="ПІБ" className="w-full border p-2 rounded" required/>

          <select name="facultyKey" value={form.facultyKey} onChange={onChange} className="w-full border p-2 rounded" required>
            <option value="">Оберіть факультет</option>
            {opts.facultiesOptions.map(f => <option key={f.key} value={f.key}>{f.name}</option>)}
          </select>

          <select name="specialty" value={form.specialty} onChange={onChange} className="w-full border p-2 rounded" required>
            <option value="">{form.facultyKey ? 'Оберіть спеціальність' : 'Спочатку оберіть факультет'}</option>
            {specList.map(s => <option key={s.key} value={s.key}>{s.label}</option>)}
          </select>

          <div className="flex gap-2">
            <input name="year" type="number" min="1900" max="2100" value={form.year} onChange={onChange} placeholder="Рік" className="w-40 border p-2 rounded" required/>
            <input name="diplomaNumber" value={form.diplomaNumber} onChange={onChange} placeholder="№ диплому" className="flex-1 border p-2 rounded" required/>
          </div>

          <div className="space-y-1">
            <input ref={fileRef} name="file" type="file" accept="application/pdf,image/png,image/jpeg" onChange={onChange} className="w-full border p-2 rounded" required/>
            <div className="text-xs opacity-70">Дозволено: pdf, png, jpg. Максимум: {MAX_FILE_MB} МБ.</div>
          </div>

          {progress > 0 && progress < 100 && (
              <div className="w-full h-2 bg-gray-200 rounded">
                <div className="h-2 rounded bg-blue-600" style={{width:`${progress}%`}}/>
              </div>
          )}

          <button disabled={busy} className="px-4 py-2 bg-blue-600 text-white rounded">
            {busy ? 'Завантаження…' : 'Зберегти'}
          </button>
        </form>
      </div>
  );
}
