import { api } from './api';

export const getDiplomas = (params = {}) =>
    api.get('/diplomas', { params }).then(r => r.data);

export const getDiplomaById = (id) =>
    api.get(`/diplomas/${id}`).then(r => r.data);

export const createDiploma = (formData, onUploadProgress) =>
    api.post('/diplomas', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        onUploadProgress: (e) => onProgress?.(Math.round((e.loaded * 100) / e.total))
    }).then(r => r.data);

export const updateDiploma = (id, data) =>
    api.put(`/diplomas/${id}`, data).then(r => r.data);

export const deleteDiploma = (id) =>
    api.delete(`/diplomas/${id}`).then(r => r.data);
