import React, { useEffect, useState } from 'react';
import backgroundImage from '../assets/karazin_bg.jpg';
import { Link } from 'react-router-dom';
import { api } from '../services/api';

export default function HomePage() {
  const [faculties, setFaculties] = useState([]); // [{key, name, specialties:[...]}]
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState('');

  useEffect(() => {
    let alive = true;
    api.get('/catalog/list')
        .then(r => { if (alive) setFaculties(r.data || []); })
        .catch(() => { if (alive) setErr('Не вдалося завантажити перелік факультетів'); })
        .finally(() => { if (alive) setLoading(false); });
    return () => { alive = false; };
  }, []);

  return (
      <div
          className="bg-cover bg-center min-h-screen text-white"
          style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        {/* напівпрозоре затемнення поверх фону */}
        <div className="bg-black bg-opacity-60 min-h-screen p-8">
          <h1 className="text-4xl font-bold mb-4 text-center">
            Дипломи випускників Каразінського університету
          </h1>
          <p className="text-center mb-8">
            Оберіть <span className="font-semibold">факультет</span> для перегляду доступних дипломів
          </p>

          {err && <p className="text-center text-red-300 mb-4">{err}</p>}

          {loading ? (
              <p className="text-center">Завантаження…</p>
          ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-5xl mx-auto">
                {faculties.map((f) => (
                    <Link
                        key={f.key}
                        to={`/search?faculty=${encodeURIComponent(f.key)}`}
                        className="bg-white bg-opacity-20 text-white border border-blue-400 p-4 rounded shadow
                           transition duration-300 hover:bg-blue-500 hover:bg-opacity-30 hover:border-blue-500"
                        title={f.name}
                    >
                      <div className="flex items-center justify-between">
                        <span>{f.name}</span>
                        {/* маленький бейдж кількості спеціальностей (необовʼязковий) */}
                        <span className="text-xs bg-blue-600 bg-opacity-60 px-2 py-0.5 rounded">
                    {f.specialties?.length ?? 0}
                  </span>
                      </div>
                    </Link>
                ))}
              </div>
          )}
        </div>
      </div>
  );
}
