import React, { useEffect, useState } from 'react';
import { listInvites, createInvite, revokeInvite } from '../services/inviteService';
import { getDiplomas, getFilters, verifyDiploma, deleteDiploma } from '../services/diplomaService';
import FilterBar from '../components/FilterBar';
import DiplomaTable from '../components/DiplomaTable';
import EditDiplomaModal from '../components/EditDiplomaModal';
import { useAuth } from '../hooks/useAuth';

export default function AdminPage() {
  const { isAdmin } = useAuth();
  const [q, setQ] = useState('');
  const [scope, setScope] = useState('verified');
  const [sort, setSort] = useState('createdAt');
  const [dir, setDir] = useState('desc');

  const [faculty, setFaculty] = useState('');
  const [specialty, setSpecialty] = useState('');
  const [faculties, setFaculties] = useState([]);
  const [specialties, setSpecialties] = useState([]);

  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [busy, setBusy] = useState(false);
  const [err, setErr] = useState('');

  const [invites, setInvites] = useState([]);
  const [invMinutes, setInvMinutes] = useState(10);

  const [editId, setEditId] = useState(null);
  const editRow = items.find(x => x.id === editId) || null;

  const loadFilters = async () => {
    try {
      const f = await getFilters();
      setFaculties(f.faculties || []);
      // если выбран факультет — ограничиваем список спеціальностей
      setSpecialties(f.specialtiesByFaculty?.[faculty] || f.dbSpecialties || []);
    } catch {}
  };

  const fetchDiplomas = async () => {
    setLoading(true);
    setErr('');
    try {
      const params = {
        q, faculty, specialty,
        verified: scope === 'all' ? undefined : (scope === 'verified' ? 'true' : 'false'),
        sort, dir, page: 1, limit: 50,
      };
      const data = await getDiplomas(params);
      setItems(data.items || []);
      setTotal(data.total || 0);
    } catch (e) {
      setErr(e.response?.data?.message || 'Load error');
    } finally { setLoading(false); }
  };

  const fetchInvites = async () => { try { setInvites(await listInvites()); } catch {} };

  useEffect(() => { loadFilters(); fetchInvites(); /* eslint-disable-next-line */ }, []);
  useEffect(() => { fetchDiplomas(); /* eslint-disable-next-line */ }, [q, faculty, specialty, scope, sort, dir]);

  const onVerify = async (id, val) => { setBusy(true); try { await verifyDiploma(id, val); await fetchDiplomas(); } finally { setBusy(false); } };
  const onDelete = async (id) => {
    if (!confirm('Видалити запис?')) return;
    setBusy(true); try { await deleteDiploma(id); await fetchDiplomas(); } finally { setBusy(false); }
  };

  const addInvite = async () => { setBusy(true); try { await createInvite(Number(invMinutes) || 10); await fetchInvites(); } finally { setBusy(false); } };
  const copyInvite = async (code) => { await navigator.clipboard.writeText(code); alert('Код скопійовано'); };
  const revoke = async (id) => { if (!confirm('Відізвати інвайт?')) return;
    setBusy(true); try { await revokeInvite(id); await fetchInvites(); } finally { setBusy(false); } };

  return (
      <div className="space-y-8">
        <h1 className="text-2xl font-bold">Адмін-панель</h1>

        <FilterBar
            q={q} setQ={setQ}
            scope={scope} setScope={setScope}
            sort={sort} setSort={setSort}
            dir={dir} setDir={setDir}
            faculty={faculty} setFaculty={(v)=>{ setFaculty(v); loadFilters(); }}
            specialty={specialty} setSpecialty={setSpecialty}
            faculties={faculties} specialties={specialties}
            onRefresh={fetchDiplomas}
            allowUnverified={isAdmin}
        />

        <DiplomaTable
            items={items}
            total={total}
            loading={loading}
            error={err}
            busy={busy}
            onVerify={(id, val)=>onVerify(id, val)}
            onDelete={onDelete}
            onEdit={(row)=>setEditId(row.id)}
            showActions={true}
        />

        {/* Інструменти */}
        <div className="space-y-2">
          <h2 className="text-xl font-semibold">Інструменти</h2>
          <button
              onClick={async () => {
                if (!confirm('Заповнити поле факультету за довідником спеціальностей?')) return;
                try {
                  const apiBase = (process.env.REACT_APP_API_URL || 'http://localhost:5000').replace(/\/+$/,'');
                  const r = await fetch(`${apiBase}/api/admin/tools/backfill-faculty`, {
                    method: 'POST',
                    headers: { Authorization: `Bearer ${localStorage.getItem('token') || ''}` }
                  });
                  const data = await r.json();
                  alert(`Оновлено: ${data.updated}\nПропущено: ${data.skipped}\nВсього: ${data.total}`);
                  fetchDiplomas();
                } catch (e) { alert('Помилка запуску бэкфіллу'); }
              }}
              className="px-3 py-2 bg-indigo-600 text-white rounded"
          >
            Заповнити факультети (масово)
          </button>
        </div>

        {/* Інвайти */}
        <div className="overflow-x-auto">
          <h2 className="text-xl font-semibold mb-2">Інвайти</h2>
          <div className="flex gap-2 items-center mb-2">
            <input type="number" min="1" value={invMinutes} onChange={e=>setInvMinutes(e.target.value)} className="border p-2 rounded w-24" />
            <span>хвилин</span>
            <button disabled={busy} onClick={addInvite} className="px-3 py-2 bg-blue-600 text-white rounded">Створити</button>
          </div>
          <table className="min-w-full text-sm border">
            <thead className="bg-gray-100">
            <tr>
              <th className="p-2 border">Код</th>
              <th className="p-2 border">Діє до</th>
              <th className="p-2 border">Дії</th>
            </tr>
            </thead>
            <tbody>
            {invites.length === 0 ? (
                <tr><td colSpan={3} className="p-3">Немає активних інвайтів</td></tr>
            ) : invites.map(i => (
                <tr key={i.id} className="odd:bg-white even:bg-gray-50">
                  <td className="p-2 border font-mono">{i.code}</td>
                  <td className="p-2 border">{i.expiresAt ? new Date(i.expiresAt).toLocaleString() : 'безстроково'}</td>
                  <td className="p-2 border space-x-2">
                    <button onClick={()=>copyInvite(i.code)} className="px-2 py-1 bg-gray-200 rounded">Копіювати</button>
                    <button disabled={busy} onClick={()=>revoke(i.id)} className="px-2 py-1 bg-red-600 text-white rounded">Відізвати</button>
                  </td>
                </tr>
            ))}
            </tbody>
          </table>
        </div>

        <EditDiplomaModal
            open={!!editId}
            row={editRow}
            onClose={()=>setEditId(null)}
            onSaved={fetchDiplomas}
        />
      </div>
  );
}
