import React from 'react';
import { Link } from 'react-router-dom';

export default function DiplomaTable({ diplomas, hideNames = false }) {
  if (!diplomas.length) return <p className="text-gray-500">–ù–µ–º–∞—î —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ñ–≤</p>;

  const maskName = (name) => {
    const [first, ...rest] = name.trim().split(' ');
    const initial = first ? first[0] + '.' : '';
    return hideNames ? `${initial} ${rest.map((w) => w[0] + '.').join(' ')}` : name;
  };

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full bg-white shadow rounded">
        <thead className="bg-gray-100">
          <tr>
            <th className="px-4 py-2 text-left">–ü–Ü–ë</th>
            <th className="px-4 py-2 text-left">–°–ø–µ—Ü—ñ–∞–ª—å–Ω—ñ—Å—Ç—å</th>
            <th className="px-4 py-2 text-left">–†—ñ–∫</th>
            <th className="px-4 py-2 text-left">–ù–æ–º–µ—Ä</th>
            <th className="px-4 py-2 text-left">–î–µ—Ç–∞–ª—ñ</th>
          </tr>
        </thead>
        <tbody>
          {diplomas.map((d) => (
            <tr key={d.id} className="border-t">
              <td className="px-4 py-2">{maskName(d.studentName)}</td>
              <td className="px-4 py-2">{d.specialty}</td>
              <td className="px-4 py-2">{d.year}</td>
              <td className="px-4 py-2">{d.diplomaNumber}</td>
              <td className="px-4 py-2">
                <Link
                  to={`/diplomas/${d.id}`}
                  className="text-blue-600 hover:underline"
                >
                  üîç –ü–µ—Ä–µ–≥–ª—è–Ω—É—Ç–∏
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
