import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../services/api';
import { createDiploma } from '../services/diplomaService';
import Alert from '../components/Alert';

const MAX_MB = 10;
const ALLOWED_MIME = new Set(['application/pdf', 'image/jpeg', 'image/png']);

export default function AddDiplomaPage() {
  const [catalog, setCatalog] = useState([]);
  const [catalogLoading, setCatalogLoading] = useState(true);
  const [catalogError, setCatalogError] = useState('');

  const [faculty, setFaculty] = useState('');
  const specialties = useMemo(
      () => (catalog.find(f => f.key === faculty)?.specialties || []),
      [catalog, faculty]
  );
  const [specialtyCode, setSpecialtyCode] = useState('');
  const specialtyName = useMemo(
      () => specialties.find(s => s.code === specialtyCode)?.name || '',
      [specialties, specialtyCode]
  );

  const [form, setForm] = useState({ studentName: '', year: '', diplomaNumber: '' });
  const [file, setFile] = useState(null);
  const [errors, setErrors] = useState({});
  const [notice, setNotice] = useState({ type: null, text: '' });
  const [progress, setProgress] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let alive = true;
    api.get('/catalog/list')
        .then(r => { if (alive) setCatalog(r.data || []); })
        .catch(() => { if (alive) setCatalogError('Не вдалося завантажити каталог'); })
        .finally(() => { if (alive) setCatalogLoading(false); });
    return () => { alive = false; };
  }, []);

  const onChange = (e) => setForm((s) => ({ ...s, [e.target.name]: e.target.value }));

  function handleFileChange(e) {
    const f = e.target.files?.[0];
    if (!f) { setFile(null); return; }
    if (!ALLOWED_MIME.has(f.type)) {
      setErrors(prev => ({ ...prev, file: 'Дозволені тільки PDF, JPG, PNG' }));
      setFile(null); e.target.value = ''; return;
    }
    if (f.size > MAX_MB * 1024 * 1024) {
      setErrors(prev => ({ ...prev, file: `Файл > ${MAX_MB} МБ` }));
      setFile(null); e.target.value = ''; return;
    }
    setErrors(prev => ({ ...prev, file: undefined }));
    setFile(f);
  }

  const onSubmit = async (e) => {
    e.preventDefault();
    setErrors({}); setNotice({ type: null, text: '' }); setProgress(0);

    if (!specialtyName) { setErrors(p => ({ ...p, specialty: 'Оберіть спеціальність' })); return; }
    if (!file) { setErrors(p => ({ ...p, file: 'Додайте файл' })); return; }

    setLoading(true);
    try {
      const fd = new FormData();
      fd.append('studentName', form.studentName);
      fd.append('year', form.year);
      fd.append('diplomaNumber', form.diplomaNumber);
      fd.append('specialty', specialtyName);
      fd.append('file', file);

      await createDiploma(fd, setProgress);

      setNotice({ type: 'success', text: 'Диплом додано. Очікує підтвердження.' });
      setFaculty(''); setSpecialtyCode('');
      setForm({ studentName: '', year: '', diplomaNumber: '' });
      setFile(null); setProgress(0);
    } catch (err) {
      const status = err.response?.status;
      const data = err.response?.data;
      if (status === 409) {
        setNotice({ type: 'error', text: 'Такий номер і рік вже існують' });
        setErrors(prev => ({ ...prev, diplomaNumber: 'Дублікат', year: 'Дублікат' }));
      } else {
        setNotice({ type: 'error', text: data?.message || 'Помилка завантаження' });
        setErrors(data?.errors || {});
      }
    } finally { setLoading(false); }
  };

  const err = (k) => errors?.[k] && <p className="text-red-600 text-sm mt-1">{errors[k]}</p>;

  return (
      <div className="max-w-xl mx-auto bg-white p-6 shadow rounded">
        <h2 className="text-2xl font-semibold mb-4">Додати диплом</h2>

        {catalogError && <p className="text-red-600 mb-3">{catalogError}</p>}
        {notice.text && <div className="mb-3"><Alert type={notice.type || 'info'}>{notice.text}</Alert></div>}

        <form onSubmit={onSubmit} className="space-y-4">
          <div>
            <label className="block mb-1">Факультет</label>
            <select
                className="w-full border px-3 py-2 rounded"
                value={faculty}
                onChange={(e) => { setFaculty(e.target.value); setSpecialtyCode(''); }}
                disabled={catalogLoading}
            >
              <option value="">— Оберіть факультет —</option>
              {catalog.map(f => <option key={f.key} value={f.key}>{f.name}</option>)}
            </select>
          </div>

          <div>
            <label className="block mb-1">Спеціальність</label>
            <select
                className="w-full border px-3 py-2 rounded"
                value={specialtyCode}
                onChange={(e) => setSpecialtyCode(e.target.value)}
                disabled={!faculty || catalogLoading}
            >
              <option value="">— Оберіть спеціальність —</option>
              {specialties.map(s => <option key={s.code} value={s.code}>{s.code} — {s.name}</option>)}
            </select>
            {err('specialty')}
          </div>

          <div>
            <label className="block mb-1">ПІБ</label>
            <input name="studentName" value={form.studentName} onChange={onChange} required className="w-full border px-3 py-2 rounded" />
            {err('studentName')}
          </div>

          <div className="flex gap-3">
            <div className="flex-1">
              <label className="block mb-1">Рік</label>
              <input
                  name="year" type="number" min="1900" max={new Date().getFullYear()}
                  value={form.year} onChange={onChange} required className="w-full border px-3 py-2 rounded"
              />
              {err('year')}
            </div>
            <div className="flex-1">
              <label className="block mb-1">Номер диплому</label>
              <input name="diplomaNumber" value={form.diplomaNumber} onChange={onChange} required className="w-full border px-3 py-2 rounded" />
              {err('diplomaNumber')}
            </div>
          </div>

          <div>
            <label className="block mb-1">Файл (PDF/JPG/PNG, ≤{MAX_MB} МБ)</label>
            <input type="file" accept=".pdf,.jpg,.jpeg,.png" onChange={handleFileChange} />
            {err('file')}
          </div>

          {loading && (
              <div className="w-full bg-gray-200 rounded h-3">
                <div className="h-3 rounded bg-blue-600" style={{ width: `${progress}%` }} />
                <p className="text-sm mt-1">{progress}%</p>
              </div>
          )}

          <button disabled={loading} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50">
            {loading ? 'Завантаження…' : 'Завантажити диплом'}
          </button>
        </form>
      </div>
  );
}
