import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { getFacultiesTree } from '../services/diplomaService';

export default function FacultyPage() {
    const { name } = useParams();
    const nav = useNavigate();
    const [node, setNode] = useState(null);
    const [err, setErr] = useState('');

    useEffect(() => {
        (async () => {
            try {
                const { faculties } = await getFacultiesTree();
                const f = (faculties || []).find(x => x.faculty === decodeURIComponent(name));
                setNode(f || null);
            } catch (e) {
                setErr(e.response?.data?.message || 'Load error');
            }
        })();
    }, [name]);

    if (err) return <div className="text-red-600">{err}</div>;
    if (!node) return <div>Немає даних</div>;

    const faculty = node.faculty;

    return (
        <div className="space-y-6">
            <div className="flex items-center gap-3">
                <h1 className="text-2xl font-bold">{faculty}</h1>
                <Link className="text-blue-600 underline" to="/">← До факультетів</Link>
                <button
                    onClick={() => nav(`/search?faculty=${encodeURIComponent(faculty)}`)}
                    className="px-2 py-1 border rounded"
                >
                    Показати всі записи факультету
                </button>
            </div>

            <h2 className="text-lg font-semibold">Спеціальності</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-3">
                {(node.specialties || []).map(s => (
                    <Link
                        key={s.specialty}
                        to={`/search?faculty=${encodeURIComponent(faculty)}&specialty=${encodeURIComponent(s.specialty)}`}
                        className="border rounded p-4 bg-white/70 hover:bg-white transition"
                    >
                        <div className="font-semibold">{s.specialty}</div>
                        <div className="text-sm opacity-70">Записів: {s.count}</div>
                    </Link>
                ))}
                {(!node.specialties || node.specialties.length === 0) && <div>Немає спеціальностей</div>}
            </div>
        </div>
    );
}
