import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../services/api';
import { createDiploma } from '../services/diplomaService';

export default function AddDiplomaPage() {
  const [catalog, setCatalog] = useState([]); // [{key,name,specialties:[{code,name}]}]
  const [catalogLoading, setCatalogLoading] = useState(true);
  const [catalogError, setCatalogError] = useState('');

  const [faculty, setFaculty] = useState('');
  const specialties = useMemo(
      () => (catalog.find(f => f.key === faculty)?.specialties || []),
      [catalog, faculty]
  );
  const [specialtyCode, setSpecialtyCode] = useState('');
  const specialtyName = useMemo(
      () => specialties.find(s => s.code === specialtyCode)?.name || '',
      [specialties, specialtyCode]
  );

  const [form, setForm] = useState({ studentName: '', year: '', diplomaNumber: '' });
  const [file, setFile] = useState(null);
  const [errors, setErrors] = useState({});
  const [msg, setMsg] = useState('');
  const [progress, setProgress] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let alive = true;
    api.get('/catalog/list')
        .then(r => { if (alive) setCatalog(r.data || []); })
        .catch(() => { if (alive) setCatalogError('Не вдалося завантажити каталог'); })
        .finally(() => { if (alive) setCatalogLoading(false); });
    return () => { alive = false; };
  }, []);

  const onChange = (e) => setForm((s) => ({ ...s, [e.target.name]: e.target.value }));

  const onSubmit = async (e) => {
    e.preventDefault();
    setErrors({}); setMsg(''); setProgress(0);

    if (!specialtyName) {
      setErrors(prev => ({ ...prev, specialty: 'Оберіть спеціальність' }));
      return;
    }

    setLoading(true);
    try {
      const fd = new FormData();
      fd.append('studentName', form.studentName);
      fd.append('year', form.year);
      fd.append('diplomaNumber', form.diplomaNumber);
      fd.append('specialty', specialtyName);
      if (file) fd.append('file', file);

      await createDiploma(fd, setProgress);

      setMsg('✅ Диплом додано. Очікує підтвердження.');
      setFaculty('');
      setSpecialtyCode('');
      setForm({ studentName: '', year: '', diplomaNumber: '' });
      setFile(null);
      setProgress(0);
    } catch (err) {
      const data = err.response?.data;
      setMsg(data?.message || 'Помилка завантаження');
      setErrors(data?.errors || {});
    } finally {
      setLoading(false);
    }
  };

  const err = (k) => errors?.[k] && <p className="text-red-600 text-sm mt-1">{errors[k]}</p>;

  return (
      <div className="max-w-xl mx-auto bg-white p-6 shadow rounded">
        <h2 className="text-2xl font-semibold mb-4">Додати диплом</h2>

        {catalogError && <p className="text-red-600 mb-3">{catalogError}</p>}
        {msg && <div className="mb-3">
          {/✅/.test(msg) ? <p className="text-green-700">{msg}</p> : <p className="text-red-600">{msg}</p>}
        </div>}

        <form onSubmit={onSubmit} className="space-y-4">
          <div>
            <label className="block mb-1">Факультет</label>
            <select
                className="w-full border px-3 py-2 rounded"
                value={faculty}
                onChange={(e) => { setFaculty(e.target.value); setSpecialtyCode(''); }}
                disabled={catalogLoading}
            >
              <option value="">— Оберіть факультет —</option>
              {catalog.map(f => (
                  <option key={f.key} value={f.key}>{f.name}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block mb-1">Спеціальність</label>
            <select
                className="w-full border px-3 py-2 rounded"
                value={specialtyCode}
                onChange={(e) => setSpecialtyCode(e.target.value)}
                disabled={!faculty || catalogLoading}
            >
              <option value="">— Оберіть спеціальність —</option>
              {specialties.map(s => (
                  <option key={s.code} value={s.code}>{s.code} — {s.name}</option>
              ))}
            </select>
            {err('specialty')}
          </div>

          <div>
            <label className="block mb-1">ПІБ</label>
            <input name="studentName" value={form.studentName} onChange={onChange} required className="w-full border px-3 py-2 rounded" />
            {err('studentName')}
          </div>

          <div className="flex gap-3">
            <div className="flex-1">
              <label className="block mb-1">Рік</label>
              <input
                  name="year"
                  type="number"
                  min="1900"
                  max={new Date().getFullYear()}
                  value={form.year}
                  onChange={onChange}
                  required
                  className="w-full border px-3 py-2 rounded"
              />
              {err('year')}
            </div>
            <div className="flex-1">
              <label className="block mb-1">Номер диплому</label>
              <input
                  name="diplomaNumber"
                  value={form.diplomaNumber}
                  onChange={onChange}
                  required
                  className="w-full border px-3 py-2 rounded"
              />
              {err('diplomaNumber')}
            </div>
          </div>

          <div>
            <label className="block mb-1">Файл (PDF/JPG/PNG, ≤10 МБ)</label>
            <input type="file" accept=".pdf,.jpg,.jpeg,.png" onChange={(e) => setFile(e.target.files?.[0] || null)} />
            {err('file')}
          </div>

          {loading && (
              <div className="w-full bg-gray-200 rounded h-3">
                <div className="h-3 rounded bg-blue-600" style={{ width: `${progress}%` }} />
                <p className="text-sm mt-1">{progress}%</p>
              </div>
          )}

          <button disabled={loading} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50">
            {loading ? 'Завантаження…' : 'Завантажити диплом'}
          </button>
        </form>
      </div>
  );
}
