// frontend/src/services/api.js
import axios from 'axios';

const apiBase = (process.env.REACT_APP_API_URL || 'http://localhost:5000').replace(/\/+$/, '');
function withApiSuffix(base) { return `${base}/api`; }

const api = axios.create({ baseURL: withApiSuffix(apiBase) });

api.interceptors.request.use((cfg) => {
    const t = localStorage.getItem('token');
    if (t) cfg.headers.Authorization = `Bearer ${t}`;
    return cfg;
});

export const API_BASE = apiBase;
export const fileUrl = (p) => (!p ? '' : (p.startsWith('http') ? p : `${apiBase}${p}`));

export default api;
export { api };
